:root {
  --animate-duration: 1s;
}

[data-animate] {
  opacity: 0;
  transition-property: opacity, transform;
  transition-duration: var(--animate-duration), var(--animate-duration);
  transition-timing-function: ease-in-out, ease-in-out;
}

.no-js [data-animate] {
  opacity: 1;
}

[data-animate] {
  opacity: 0;
}

[ready] [data-animate] {
  opacity: 1;
}

[data-animate='fadein'] {
  opacity: 0;
}

[ready] [data-animate='fadein'] {
  opacity: 1;
}

[data-animate='fadeup'] {
  transform: translateY(5rem);
}

[ready] [data-animate='fadeup'] {
  transform: translateY(0);
}

[data-animate='fadedown'] {
  transform: translateY(-5rem);
}

[ready] [data-animate='fadedown'] {
  transform: translateY(0);
}

[data-animate='fadeleft'] {
  transform: translateX(-5rem);
}

[ready] [data-animate='fadeleft'] {
  transform: translateX(0);
}

[data-animate='faderight'] {
  transform: translateX(5rem);
}

[ready] [data-animate='faderight'] {
  transform: translateX(0);
}

[data-animate='zoomin'] {
  transform: scale(0.8);
}

[ready] [data-animate='zoomin'] {
  transform: scale(1);
}

[data-animate='zoomout'] {
  transform: scale(1.2);
}

[ready] [data-animate='zoomout'] {
  transform: scale(1);
}

[data-animate='rise-up']{
  opacity: 0;
}

[data-animate='rise-up'] [ready] [data-animate='rise-up'] {
    animation: rise-up 0.8s cubic-bezier(0.26, 0.54, 0.32, 1) forwards;
  }

.image-wrap {
  background: var(--color-small-image-bg);
  overflow: hidden;
}

.image-wrap img:not([role='presentation']) {
  display: block;
}

.unload:before,
.unload:after {
  content: '';
  position: absolute;
  width: 100px;
  height: 3px;
  background: var(--color-body);
  left: 50%;
  top: 30%;
  margin: -1px 0 0 -50px;
  z-index: var(--z-index-loader);
  opacity: 0;
}

.no-js .unload:before, .no-js .unload:after {
    display: none;
  }

.unload:before {
  background: var(--color-text-body);
  opacity: 1;
}

.unload:after {
  opacity: 0;
  animation: preloading 0.5s ease 0.3s infinite;
}

.loading {
  animation: placeholder-shimmer 1.3s linear 0.5s infinite;
  background-size: 300% 100%;
  background-image: linear-gradient(
    100deg,
    var(--color-large-image-bg) 40%,
    var(--color-large-image-bg-light) 63%,
    var(--color-large-image-bg) 79%
  );
}

.loading.loaded {
    animation: none;
    background-image: none;
  }

.loading--delayed:before {
  animation-delay: 0.8s !important;
  animation-duration: 1s !important;
}

.loading--delayed:after {
  animation-delay: 1.3s !important;
}

.clearfix::after {
  clear: both;
  content: '';
  display: table;
}

.is-transitioning {
  display: block !important;
  visibility: visible !important;
}

.visually-hidden {
  border: 0;
  clip: rect(0 0 0 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}

.overlay::after {
  content: '';
  display: block;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: var(--z-index-overlay, auto);
}

/*Keep dimensions but hide visually*/

.visually-invisible {
  opacity: 0 !important;
}

.show {
  display: block !important;
}

.hide {
  display: none !important;
}

.text-left {
  text-align: left !important;
}

.text-right {
  text-align: right !important;
}

.text-center {
  text-align: center !important;
}

@media only screen and (max-width: 768px) {
  .small--show {
    display: block !important;
  }
  .small--hide {
    display: none !important;
  }
  .small--text-left {
    text-align: left !important;
  }
  .small--text-right {
    text-align: right !important;
  }
  .small--text-center {
    text-align: center !important;
  }
}

