picture img {
  width: 100%;
  max-height: 100%;
  min-height: inherit;
}

/* TODO: this should be part of a more generalized media file */

.content-over-media iframe {
  position: absolute;
  top: 50%;
  width: 100%;
  transform: translateY(-50%);
