}

.tag__checkbox:before,
  .tag__checkbox:after {
    content: '';
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
  }

.tag__checkbox:before {
    border: 1px solid;
    border-color: var(--color-border);
    height: var(--tag-active-icon-size);
    width: var(--tag-active-icon-size);
