    content: '';
    width: 60%;
    height: 35%;
    top: 0;
    left: 100%;
  }

.product__video-wrapper__grippy + .product__video-wrapper__grippy {
    left: auto;
    right: 0;
  }

.product__video-wrapper__grippy + .product__video-wrapper__grippy:before {
      bottom: 0;
      top: auto;
      right: 100%;
      left: auto;
