}

.results-products__image {
  width: 100%;
  min-width: 80px;
  max-width: 80px;
  height: 80px;
}

.results-products__info {
  display: flex;
  flex-direction: column;
}

.predictive-search-results {
  display: flex;
  flex-wrap: wrap;
  flex-direction: column;
  overflow-y: auto;
  padding: 40px 0 0;
}

.predictive-search-results h3 {
    padding-bottom: 10px;
    border-bottom: 1px solid;
    border-color: var(--color-border);
    margin-bottom: 10px;
