.slideshow-wrapper {
  position: relative;
}

/*Pause button (focusable by keyboard only)*/

.slideshow__pause:focus {
  clip: auto;
  width: auto;
  height: auto;
  margin: 0;
  color: var(--color-button-primary-text);
  background-color: var(--color-button-primary);
  padding: 10px;
  z-index: 10000;
  transition: none;
}

.video-is-playing .slideshow__pause:focus {
    display: none;
  }

.slideshow__pause-stop {
  display: block;
}

.is-paused .slideshow__pause-stop {
    display: none;
  }

.slideshow__pause-play {
  display: none;
}

.is-paused .slideshow__pause-play {
    display: block;
  }

.slideshow__slide {
  display: none;
  width: 100%;
  height: 100%;
  position: relative;
  overflow: hidden;
}

.slideshow__slide:first-child {
    display: block;
  }

.flickity-slider .slideshow__slide {
    display: block;
  }

.hero--padded .slideshow__slide {
    border-radius: var(--roundness);
    overflow: hidden;
  }

.hero__image-wrapper--no-overlay:before {
  content: none;
}

/* Hero side by side layout */

.hero__sidebyside {
  height: 100%;
  display: flex;
}

.hero__sidebyside.color-scheme-none {
    background-color: var(--color-body);
    color: var(--color-text-body);
  }

.hero__sidebyside-content {
  position: relative;
}

.hero__sidebyside-content-inner {
  align-self: center;
  flex: 1;
  padding: 30px;
}

.hero__sidebyside-image {
  overflow: hidden;
}

.hero__sidebyside-image-link {
  display: block;
  height: 100%;
}

.hero__sidebyside-image--indented .hero__image {
    padding: 30px 30px 0;
    object-fit: contain;
  }

.hero__sidebyside-image--indented .placeholder-svg {
    margin: 30px 30px 0;
    height: calc(100% - 60px);
    width: calc(100% - 60px);
  }

@media only screen and (max-width: 768px) {
  .hero__sidebyside {
    flex-direction: column;
  }

  .hero__sidebyside-image {
    position: relative;
    flex: 1 1 auto;
  }

    .hero__sidebyside-image .hero__image,
    .hero__sidebyside-image .placeholder-svg {
      position: absolute;
      top: 0;
      left: 0;
    }

  .hero__sidebyside-content {
    flex: 0 1 auto;
    display: flex;
    order: 2;
  }
}

@media only screen and (min-width: 769px) {
  .hero__sidebyside-content {
    flex: 1 1 auto;
    display: flex;
    max-width: 40%;
  }

  .hero__sidebyside-content-inner {
    padding: 5vw;
  }
    .hero__sidebyside-text--right .hero__sidebyside-content {
      order: 2;
    }
    .hero__sidebyside-text--left .hero__sidebyside-content-inner--indented {
      padding-right: 0;
    }

    .hero__sidebyside-text--right .hero__sidebyside-content-inner--indented {
      padding-left: 0;
    }

  .hero__sidebyside-image {
    flex: 0 1 60%;
  }
    .hero__sidebyside-image--indented .hero__image {
      padding: 80px;
      object-position: center right;
    }

  .hero__sidebyside-text--right .hero__sidebyside-image--indented .hero__image {
    object-position: center left;
  }
}

.tool-tip-trigger {
  background: none;
  border: 0;
  cursor: pointer;
}

.tool-tip-trigger__title {
  display: inline-block;
}

.tool-tip-trigger__title:hover,
.tool-tip-trigger__title:focus {
  -webkit-text-decoration: underline;
  text-decoration: underline;
  text-underline-offset: 2px;
}

.tool-tip-trigger__content {
  display: none !important; /* Ensure our content is always hidden */
}

/* Size chart trigger */

.size-chart__standalone {
  margin: var(--size-chart-margin);
}

.size-chart__standalone svg {
    margin-left: var(--size-chart-icon-margin);
  }

[data-tool-tip='size-chart'] .tool-tip__close {
  border: 0;
  padding: 6px;
  position: absolute;
  top: 0;
  right: 0;
  transform: translate(25%, -25%);
  z-index: 2;
  color: var(--color-button-primary-text);
  background: var(--color-button-primary);
  border-radius: 50%;
}

tool-tip {
  display: none;
}

tool-tip.quick-shop-modal .page-content--product {
      max-width: 100%;
      width: 1500px;
    }

/*Ajax page adjustments*/

tool-tip .page-content,
  tool-tip .page-width {
    padding: 0;
  }

tool-tip[data-tool-tip-open='true'] {
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10001;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

tool-tip[data-tool-tip-open='true']:before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #e0e0e0;
    background-color: var(--color-modal-bg);
    animation: overlay-on 0.3s forwards;
    cursor: pointer;
  }

.tool-tip__inner {
  animation: modal-close 0.3s forwards;
  opacity: 0;
  color: var(--color-text-body);
}

tool-tip[data-tool-tip-open='true'] .tool-tip__inner {
    animation: modal-open 0.3s forwards;
    display: block;
    position: fixed;
    background: var(--color-body);
    box-shadow: 0 10px 20px #00000017;
    padding: 30px;
    min-width: 250px;
    min-height: 250px;
  }

@media only screen and (max-width: 768px) {

tool-tip[data-tool-tip-open='true'] .tool-tip__inner {
      width: calc(100% - 20px);
      max-width: 500px;
  }
    }

@media only screen and (min-width: 769px) {

.tool-tip__inner {
    margin: 40px;
    max-width: calc(100% - 80px);
    max-height: 90vh;
    padding: calc(var(--gutter) * 1.5);
}

    .quick-shop-modal .tool-tip__inner {
      max-width: 1200px;
      width: calc(100% - 80px);
    }
  }

.tool-tip__content {
  max-height: 80vh;
  overflow: auto;
}

.quick-shop-modal .tool-tip__content,
  .quick-add-modal .tool-tip__content {
    padding: 1rem;
  }

[data-tool-tip='store-availability'] .tool-tip__content {
    padding: 0;
  }

@media only screen and (max-width: 768px) {

.tool-tip__content {
    font-size: 0.85em;
}
  }

.tool-tip__close {
  position: absolute;
  top: 0;
  right: 0;
  border: 0;
  padding: 6px;
  transform: translate(25%, -25%);
  z-index: 2;
  transition: transform 0.15s ease-out;
}

.tool-tip__close:hover {
    transform: translate(25%, -25%) scale(1.08);
  }

.tool-tip__close .icon {
  width: 28px;
  height: 28px;
}

.store-availability {
  margin-top: var(--gutter);
  display: flex;
  justify-content: space-around;
}

.store-availability .icon {
    margin: 6px 0 0;
    width: 12px;
    height: 12px;
  }

.store-availability .icon-in-stock {
    fill: var(--success-green);
  }

.store-availability .icon-out-of-stock {
    fill: var(--error-red);
  }

.store-availability + .store-availability {
    margin-top: 20px;
  }

.store-availability__info {
  flex: 0 1 90%;
  text-align: left;
  margin-left: 10px;
}

.store-availability__info > div {
    margin-bottom: 5px;
  }

.store-availability__info > div:last-child {
      margin-bottom: 0;
    }

.store-availability__info a {
    -webkit-text-decoration: underline;
    text-decoration: underline;
  }

.store-availability__small {
  font-size: 0.8em;
}

.store-availability__small a {
    display: block;
    margin-top: 10px;
  }

.quick-shop-modal .store-availability__small--link {
    display: none;
  }

/*
 * This is the stylesheet entrypoint for the testimonials section.
 * This file and all its inclusions will be processed through postcss
 */

.quote-icon {
  display: block;
  margin: 0 auto 20px;
}

.testimonial-stars {
  display: block;
  font-size: 16px;
  letter-spacing: 0.2em;
  margin-bottom: 10px;
}

@media only screen and (min-width: 769px) {

.testimonial-stars {
    font-size: 18px;
    margin-bottom: 15px;
}
  }

.testimonials-slide {
  display: none;
  opacity: 0;
  padding: 0 0 55px;
  width: 33%;

  /*Hide all but first slide to prevent page reflows prior to init*/
}

.testimonials-slide:first-child {
    display: block;
  }

.flickity-slider .testimonials-slide {
    display: block;
    opacity: 1;
  }

.testimonials-slide .testimonials-slider__text {
    transform: scale(0.95);
    transition:
      transform 0.5s ease, box-shadow 0.5s ease;
  }

.testimonials-slide.is-selected .testimonials-slider__text {
    transform: scale(1.1);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
    position: relative;
    z-index: 10;
  }

@media only screen and (max-width: 768px) {
    .testimonials-slide {
      width: 100%;
      padding: 0 0 25px;
    }

    .testimonials-slide .testimonials-slider__text {
      transform: scale(0.86);
    }

    .testimonials-slide.is-selected .testimonials-slider__text {
      transform: scale(1);
    }
  }

.testimonials-slider__text {
  margin: 0 30px;
  padding: 30px calc(var(--gutter) / 2);
  background: var(--color-body);
  color: var(--color-text-body);
  margin-bottom: calc(var(--gutter) / 2);
}

@media only screen and (min-width: 769px) {

.testimonials-slider__text {
    margin: 0;
    padding: 30px;
    margin-bottom: 0;
}
  }

.testimonials-slider__text cite {
    font-style: normal;
    font-weight: var(--type-header-weight);
  }

@media only screen and (min-width: 769px) {

.testimonials-slider__text cite {
      font-size: calc(var(--type-base-size) + 1px);
  }
    }

.testimonials-slider__text p {
    margin-bottom: calc(var(--gutter) / 4);
  }

.testimonials-slider__text p + cite {
      margin-top: 0;
    }

.testimonials__info {
  font-size: calc(var(--type-base-size) - 1px);
}

:root {
  testimonial-image-size: 142px;
  testimonial-image-size-round: 65px;
}

.testimonial-image {
  max-width: var(testimonial-image-size);
  background-color: var(--color-body);
}

.text-right .testimonial-image {
    margin-left: auto;
  }

.text-center .testimonial-image {
    margin-left: auto;
    margin-right: auto;
  }

.testimonial-image .image-wrap {
    background: none;
    width: 100%;
    height: 100%;
  }

.testimonial-image--round {
  width: var(testimonial-image-size-round);
  height: var(testimonial-image-size-round);
  max-width: none;
  border-radius: var(testimonial-image-size-round);

  /*fix animation bug in Safari*/
}

.testimonial-image--round img {
    overflow: hidden;
    border-radius: var(testimonial-image-size-round);
    height: 100%;
  }

.testimonials-section {
  display: block;
  padding: var(--index-section-padding) 0;
}

.testimonials-section .flickity-page-dots {
  bottom: 0;
}

.feature-row-wrapper {
  overflow: hidden;
  direction: ltr;
}

.feature-row {
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

@media (--widescreen) {

.feature-row {
    margin: 0 6%;
}
  }

@media only screen and (max-width: 768px) {

.feature-row {
    flex-direction: column;
    margin: 0;
}
  }

@media only screen and (max-width: 959px) {
  .feature-row--small-none {
    display: block;
  }
}

.feature-row__item {
  flex: 0 1 57%;
  margin: 0 auto;
}

@media only screen and (max-width: 768px) {

.feature-row__item {
    flex: 1 1 auto;
    max-width: 100%;
    min-width: 100%;
}
  }

.feature-row__image {
  display: block;
  margin: 0 auto;
  order: 1;
}

@media only screen and (min-width: 769px) {

.feature-row__image {
    order: 2;
}
  }

.feature-row__text {
  padding: 0;
}

@media only screen and (max-width: 768px) {

.feature-row__text {
    order: 2;
    margin-top: 0;
    padding: 30px 20px 0;
    padding-bottom: 0; /*always last element on mobile*/
}
  }

.feature-row__text .rte {
    margin: 0;
  }

.feature-row__text .btn {
    margin-top: calc(var(--gutter) / 2);
  }

@media only screen and (min-width: 769px) {
  .feature-row__text--left {
    padding-left: calc(var(--gutter) * 2);
    padding-right: 20px;
  }

  .feature-row__text--right {
    padding-right: calc(var(--gutter) * 2);
    padding-left: 20px;
  }
}

.feature-row__item--overlap-images {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 0 15px;
  margin: 0 0 0 -30px;
}

@media only screen and (min-width: 769px) {

.feature-row__item--overlap-images {
    padding: 50px 0;
    margin: 0 auto;
}
  }

.feature-row__item--overlap-images > *:first-child {
    z-index: 1;
    transform: translate(30px, 30px);
  }

@media only screen and (min-width: 769px) {

.feature-row__item--overlap-images > *:first-child {
      transform: translate(50px, 50px);
  }
    }

.feature-row__item--overlap-images svg {
    border: 2px solid;
    border-color: var(--color-body);
  }

.feature-row__item--overlap-images .feature-row__first-image {
    width: 55%;
  }

.feature-row__item--overlap-images .feature-row__second-image {
    width: 60%;
  }

.feature-row__item--placeholder-images .placeholder-image-wrap {
    width: 50%;
  }

.feature-row-wrapper .feature-row__images {
  width: 100%;
  min-width: 0;
}

@media only screen and (max-width: 959px) {

.feature-row-wrapper .feature-row__images {
    padding-top: 30px;
}
  }

.feature-row-wrapper .feature-row__text {
  width: 100%;
  min-width: 0;
}

@media only screen and (min-width: 769px) {

.feature-row-wrapper .feature-row__text {
    min-width: 43%;
    flex: 0 1 43%;
}
  }

.feature-row--33 .feature-row__images {
    max-width: 50%;
  }

@media only screen and (min-width: 769px) {

.feature-row--33 .feature-row__images {
      max-width: none;
      flex: 0 1 33.333%;
  }
    }

.feature-row--50 .feature-row__images {
    max-width: 100%;
    padding-right: 20px;
    padding-left: 20px;
  }

@media only screen and (min-width: 769px) {

.feature-row--50 .feature-row__images {
      max-width: none;
      flex: 0 1 50%;
      padding-right: 0;
      padding-left: 0;
  }
    }

.feature-row--66 .feature-row__images {
    max-width: 100%;
    padding-right: 20px;
    padding-left: 20px;
  }

@media only screen and (min-width: 769px) {

.feature-row--66 .feature-row__images {
      max-width: none;
      flex: 0 1 66.666%;
      padding-right: 0;
      padding-left: 0;
  }
    }

