.toolbar {
  position: relative;
  background: var(--color-toolbar);
  color: var(--color-toolbar-text);
  font-size: calc(var(--type-base-size) * 0.85);
}

.toolbar a {
    color: var(--color-toolbar-text);
  }

.is-light .toolbar {
    background-color: transparent;
    color: var(--color-sticky-nav-links);
  }

.is-light .toolbar a {
      color: var(--color-sticky-nav-links);
    }

.toolbar:after {
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    content: '';
    display: block;
    border-bottom: 1px solid;
    border-bottom-color: var(--color-toolbar-text);
    width: 100%;
    opacity: 0.15;
  }

.is-light .toolbar:after {
      border-color: var(--color-sticky-nav-links);
    }

.toolbar__content {
  display: flex;
  justify-content: flex-end;
  align-items: center;
}

.toolbar__item {
  flex: 0 1 auto;
  padding: 0 5px;
}

.toolbar__item:first-child {
    padding-left: 0;
  }

.toolbar__item:last-child {
    padding-right: 0;
  }

.toolbar__item select,
  .toolbar__item .faux-select {
    font-size: 14px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-left: 0;
    border: 0;
    background: transparent;
    color: currentColor;
  }

.toolbar__item--announcements {
  flex: 1 1 auto;
}

.toolbar__social {
  text-align: right;
}

.toolbar__social a {
    display: block;
    padding: 5px;
  }

.toolbar__social .icon {
    position: relative;
    vertical-align: middle;
    width: 16px;
    height: 16px;
  }

.social-sharing__title {
  font-size: calc(var(--type-base-size) * 0.85);
  display: inline-block;
  vertical-align: middle;
  padding-right: 15px;
  padding-left: 3px;
}

.footer__social {
  margin: 0;
}

form + .footer__social {
    margin-top: var(--gutter);
  }

.footer__social li {
    display: inline-block;
    margin: 0 15px 15px 0;
  }

.footer__social a {
    display: block;
  }

.footer__social .icon {
    width: 21px;
    height: 21px;
  }

.footer__social .icon.icon--wide {
      width: 40px;
    }

.social-sharing {
  display: inline-flex;
  flex-wrap: wrap;
  gap: 10px 20px;
}

.social-sharing .icon {
    height: 18px;
    width: 18px;
  }

.blog-layout__sidebar .social-sharing {
    margin-bottom: 40px;
  }

.social-sharing__link {
  display: inline-block;
  color: var(--color-text-body);
  border-radius: 2px;
  margin: 0 18px 0 0;
  -webkit-text-decoration: none;
  text-decoration: none;
  font-weight: normal;
}

.social-sharing__link:last-child {
    margin-right: 0;
  }

.variant__label-info {
  /*undo accent stack styles*/
  text-transform: none;
  font-weight: normal;
  letter-spacing: 0;
}

:root {
  --label-bottom-margin: 12px;
}

.variant-button-wrap {
  border: 0;
  padding: 0;
  margin: 0 0 calc(var(--label-bottom-margin) * -1);
  position: relative;
}

.variant-button-wrap label {
    font: inherit;
    position: relative;
    display: inline-block;
    font-weight: normal;
    padding: 7px 15px 7px;
    margin: 0 8px var(--label-bottom-margin) 0;
    background-color: var(--color-body);
    box-shadow: 0 0 0 1px var(--color-border);
    border-radius: 1px;
    overflow: hidden;
  }

.variant-button-wrap label[data-color-swatch] {
      padding: 0;
      background-color: var(--swatch-bg-color);
      background-image: var(--swatch-bg-image);
    }

.variant-button-wrap input[type='radio']:focus + label {
    box-shadow: 0 0 0 1px var(--color-text-body);
    outline: 2px auto Highlight;
    outline: 2px auto -webkit-focus-ring-color;
    outline-offset: 1px;
  }

.variant-button-wrap input[type='radio']:checked + label {
    box-shadow: 0 0 0 2px var(--color-text-body);
  }

.variant-button-wrap input[data-disabled] + label {
    color: var(--color-border);
  }

.variant-button-wrap input[data-disabled] + label:after {
    position: absolute;
    content: '';
    left: 50%;
    top: 0;
    bottom: 0;
    border-left: 2px solid;
    border-color: var(--color-border);
    transform: rotate(45deg);
  }

.variant-button-wrap .variant__label,
  .variant-button-wrap .variant__label[for] {
    display: block;
    margin-bottom: 10px;
    cursor: default;
  }

.variant-wrapper + .variant-wrapper {
  margin-top: var(--size-6)
}

video-media {
  --default-aspect-ratio: 16 / 9;

  display: block;
  position: relative;
  height: 100%;
  aspect-ratio: var(--aspect-ratio, var(--default-aspect-ratio));
}

video-media > video, video-media > iframe, video-media > img, video-media > svg {
  border-radius: inherit;
  width: 100%;
  height: 100%;
  transition:
    opacity 0.2s ease-in-out, visibility 0.2s ease-in-out;
}

video-media > img,
video-media > svg,
video-media > video:not(:fullscreen) {
  object-fit: cover;
  object-position: center;
}

.video-media[data-background='true'] > video-media {
  position: absolute;
  height: 100%;
  width: 100%;
}

video-media:not([loaded]) > video, video-media:not([loaded]) > iframe, video-media[loaded] > img, video-media[loaded] > svg {
  visibility: hidden;
  opacity: 0;
}
