+
+/* Grid */
+    .grid {
+        display: flex;
+        flex-wrap: wrap;
+        gap: 20px;
+    }
+
+    .grid-section__header{
+        text-align: center;
+        margin-bottom: 40px;
+
+    }
+
+    .section-{{ section.id }} .grid-section__header{
+      {% if section.settings.header_inline == true %}
+            display: flex;
+            justify-content: space-between;
+            align-items: center;
+        {% endif %}
+    }
+
+    .grid-section__header img{
+      margin: auto;
+    }
+
+    .grid-section__header h2{
+        font-family: "BasicSans";
+        font-size: 35px;
+        font-weight: 400;
+    }
+
+    .grid__item {
+        position: relative;
+        /* max-height: 570px; */
+        /* overflow: hidden; */
+
+    }
+
+    .grid-section__header-intro{
+        font-size: 16px;
+        margin-bottom: 0;
+    }
+
+    .grid__item img {
+        width: 100%;
+        height: auto;
+        object-fit: cover;
+        /* display: block; */
+    }
+
+    .section-{{ section.id }} .grid__item img{
+        border-radius: {{ section.settings.border_radius }}px;
+    }
+
+    .grid-section .button{
+      margin-top: 40px;
+    }
+
+    /* Flex-based width handling */
+    .grid__item--full {
+        flex: 0 0 100%;
+    }
+
+    .grid__item--half {
+        flex: 0 0 calc(50% - 10px);
+    }
+
+    .grid__item--third {
+        flex: 0 0 calc(33.3% - (40px / 3));
+    }
+
+    .grid__item--sixty {
+        flex: 0 0 calc(60% - 10px);
+    }
+
+    .grid__item--forty {
+        flex: 0 0 calc(40% - 10px);
+    }
+
+    .grid-section__header-desc > *{
+      font-weight: 400;
+    }
+
+    /* Responsive adjustments */
+    @media (max-width: 768px) {
+        .grid__item--full,
+        .grid__item--half,
+        .grid__item--third,
+        .grid__item--sixty,
+        .grid__item--forty {
+            flex: 0 0 100%;
+        }
+    }
+
+    .grid__item-content{
+        position: relative;
+        /* text-align: var(--grid-text-alignment); */
+    }
+
+
+    .grid__item-content h3 {
+        margin: 0;
+        font-size: 48px;
+        font-weight: 700;
+        margin-top: 16px;
+    }
+
+    .grid__item-content h4 {
+      font-size: 20px;
+      font-weight: 400;
+      margin-top: 16px;
+    }
+
+    .grid__item-content p {
+        margin: 0;
+        font-size: 16px;
+        color: rgba(38, 38, 38, 0.65);
+        font-weight: 400;
+    }
+
+    /* Content Overlay */
+    .grid__item-content.grid__item-content--overlap {
+        position: absolute;
+        top: 0;
+        bottom: 0;
+        right: 0;
+        left: 0;
+        width: 100%;
+        padding: 40px;
+    }
+
+
+    .grid__item-content.grid__item-content--overlap h3 {
