+    }
+
+
+  @media screen and (max-width: 699px){
+
+  .grid__item-content h4{
+    font-size: 16px;
+  }
+
+    .grid__item-content p{
+      font-size: 12px;
+    }
+    .grid__item-content.grid__item-content--overlap h3{
+        font-size: 18px;
+    }
+
+    .grid-section__header h2{
+      font-size: 24px;
+    }
+
+    .grid__item-content h3{
+      font-size: 24px;
