+  /* Somos Slider */
+
+.content_section_width_standard,
+.content_section_width_full {
+transition: all 0.3s ease;
+}
+.content_section_width_full {
+width: 100%;
+}
+
+.content_section_width_standard {
+margin: 0 auto;
+width: 90%;
+}
+  .somos-slider {
+    position: relative;
+    overflow: hidden;
+  }
+  .somos-slider__wrapper {
+    display: flex;
+    transition: transform 0.5s ease;
+    width: 100%;
+  }
+  .somos-slide {
+    flex: 0 0 100%;
+    height: var(--slide-height);
+  }
+  .somos-slide__media-wrapper {
+    position: relative;
+    width: 100%;
+    height: 100%;
+  }
+  .somos-slide__media-wrapper img {
+    width: 100%;
+    height: 100%;
+    object-fit: cover;
+    display: block;
+  }
+  .somos-slide__content {
+    position: absolute;
+    top: 0;
+    left: 0;
+    right: 0;
+    bottom: 0;
+    display: flex;
+    flex-direction: column;
+    padding: 1rem;
+    text-align: center;
+  }
+  .top    { align-items: center; justify-content: flex-start; }
+  .middle { align-items: center; justify-content: center; }
+  .bottom { align-items: center; justify-content: flex-end; }
+  .left   { align-items: flex-start; text-align: left; }
+  .center { align-items: center; text-align: center; }
+  .right  { align-items: flex-end; text-align: right; }
+
+  .somos-slider__arrows {
+    position: absolute;
+    top: 50%;
+    width: 100%;
+    display: flex;
+    justify-content: space-between;
+    transform: translateY(-50%);
+    pointer-events: none;
+  }
+  .somos-slider__arrows button {
+    pointer-events: auto;
+    background: rgba(0,0,0,0.5);
+    color: #fff;
+    border: none;
+    padding: 10px 15px;
+    cursor: pointer;
+    border-radius: 50%;
+  }
+  .somos-slider__dots {
+    position: absolute;
+    bottom: 10px;
+    left: 50%;
+    transform: translateX(-50%);
+    display: flex;
+    justify-content: center;
+    gap: 10px;
+    padding: 10px 20px;
+    border-radius: 30px;
+    background: rgba(0, 0, 0, 0.10);
+    backdrop-filter: blur(7.5px);
+    z-index: 2;
+  }
+  .somos-slider__dots button {
+    width: 8px;
+    height: 8px;
+    border-radius: 50%;
+    background: #ccc;
+    border: none;
+  }
+  .somos-slider__dots button[aria-current="true"] {
+    background: #DB4B97;
+    width: 24px;
+    border-radius: 20px;
+  }
+  .somos-slide .button {
+    display: inline-block;
+    margin: 5px;
+    padding: 10px 20px;
+    text-decoration: none;
+    border-radius: 50px;
+    border: 2px solid;
+  }
+
+  /* Tab Products */
+    .tab-product-content__info {
+    display: grid;
+    place-items: var(--vertical-align, center) var(--horizontal-align, center);
+    text-align: var(--text-align, center);
+  }
+  .tab-product-content__info.place-left {
+    --horizontal-align: start;
+    --text-align: left;
+  }
+  .tab-product-content__info.place-center {
+    --horizontal-align: center;
+    --text-align: center;
+  }
+  .tab-product-content__info.place-right {
+    --horizontal-align: end;
+    --text-align: right;
+  }
+  .tab-product-content__info.align-top { --vertical-align: start; }
+  .tab-product-content__info.align-middle { --vertical-align: center; }
+  .tab-product-content__info.align-bottom { --vertical-align: end; }
+
+  .overlay-image {
+    position: absolute;
+    top: 1rem;
+    right: 1rem;
+    z-index: 10;
+  }
+
+  .product-filter-buttons {
+    display: flex;
+    gap: 0.5rem;
+    margin-top: 1rem;
+  }
+
+  .filter-btn {
+    background: white;
+    border: 1px solid #ccc;
+    padding: 0.5rem 1rem;
+    border-radius: 5px;
+    cursor: pointer;
+  }
