-    /* Flex-based width handling */
-    .grid__item--full {
-        flex: 0 0 100%;
-    }
-
-    .grid__item--half {
-        flex: 0 0 calc(50% - 10px);
-    }
-
-    .grid__item--third {
-        flex: 0 0 calc(33.3% - (40px / 3));
-    }
-
-    .grid__item--sixty {
-        flex: 0 0 calc(60% - 10px);
