+
+    }
+
+    .grid-section__header-intro{
+        font-size: 16px;
+        margin-bottom: 0;
+    }
+
+    .grid__item img {
+        width: 100%;
+        height: auto;
+        object-fit: cover;
+        /* display: block; */
