<style>
  .image-text-blocks-section img {
    margin: 0 auto;
  }
</style>

{% assign text_align = section.settings.alignment | default: 'center' %}

<div class="image-text-blocks-section px-4 md:px-8 py-6 section_width_{{ section.settings.section_width }}"
     style="
    {% if section.settings.background_gradient != blank %}background: {{ section.settings.background_gradient }};{% endif %}
    background-color: {{ section.settings.background_color }};
    padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px;
    margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;
    text-align: {{ text_align }};
">

  {% for block in section.blocks %}
    {% case block.type %}
      {% when 'image' %}
        {% if block.settings.image %}
          {% assign image_align = block.settings.image_alignment | default: 'center' %}
          <div class="section_content_width_{{ section.settings.section_content_width }} flex mb-4 {% if image_align == 'left' %} justify-start {% elsif image_align == 'right' %} justify-end {% else %} justify-center {% endif %}">
            <img src="{{ block.settings.image | image_url: width: 1200 }}" alt="Section image" class="mb-4 inline-block" loading="lazy" />
          </div>
        {% endif %}

      {% when 'title' %}
        <h2 class="text-2xl md:text-4xl font-bold mb-2">{{ block.settings.title }}</h2>

      {% when 'text' %}
        <p class="desc">{{ block.settings.text }}</p>

      {% when 'button' %}
        {% if block.settings.button_text and block.settings.button_link %}
          <div>
            <a href="{{ block.settings.button_link }}"
               class="inline-block bg-black text-white py-2 px-4 rounded hover:bg-gray-800"
               style="{% if text_align == 'center' %} display: block; margin-left: auto; margin-right: auto; {% elsif text_align == 'right' %} display: block; margin-left: auto; {% endif %}">
              {{ block.settings.button_text }}
            </a>
          </div>
        {% endif %}

    {% endcase %}
  {% endfor %}

  {% comment %}
    Image Comparison output below — uses first two comparison_image blocks
  {% endcomment %}
  {% assign compare_images = section.blocks | where: 'type', 'comparison_image' %}
  {% if compare_images.size == 2 %}
    <div class="image-compare-wrapper" style="max-width: {{ section.settings.fullwidth | default: false | if: '100%', '1200px' }}; margin: 0 auto;">
      {% render 'section-image-compare',
        image_1: compare_images[0].settings.image,
        image_2: compare_images[1].settings.image,
        heading: section.settings.heading,
        heading_size: section.settings.heading_size,
        heading_position: section.settings.heading_position,
        slider_style: section.settings.slider_style,
        height: section.settings.height,
        color: section.settings.color
      %}
    </div>
  {% endif %}
</div>
