{% comment %}
  Custom section: Article Content | article-content.liquid
{% endcomment %}

<section class="article-content" id="article-content">
  <div class="article-content__wrapper">
    <nav class="article-content__nav">
      <ul>
        <li><a href="#{{ section.settings.section_1_id }}">{{ section.settings.section_1_heading }}</a></li>
        <li><a href="#{{ section.settings.section_2_id }}">{{ section.settings.section_2_heading }}</a></li>
        <li><a href="#{{ section.settings.section_3_id }}">{{ section.settings.section_3_heading }}</a></li>
        <li><a href="#{{ section.settings.section_3_subtitle_1_id }}">{{ section.settings.section_3_subtitle_1 }}</a></li>
        <li><a href="#{{ section.settings.section_3_subtitle_2_id }}">{{ section.settings.section_3_subtitle_2 }}</a></li>
      </ul>
    </nav>
    <div class="article-content__section" id="{{ section.settings.section_1_id }}">
      <h2 class="article-content__heading">{{ section.settings.section_1_heading }}</h2>
      <p class="article-content__paragraph">{{ section.settings.section_1_description }}</p>
      <div class="article-content__image-grid">
        <div class="article-content__image-block">
          <img src="{{ section.settings.section_1_image_1 | image_url: width: 800 }}" alt="" loading="lazy">
          <h3>{{ section.settings.section_1_image_1_title }}</h3>
          <p>{{ section.settings.section_1_image_1_text }}</p>
        </div>
        <div class="article-content__image-block">
          <img src="{{ section.settings.section_1_image_2 | image_url: width: 800 }}" alt="" loading="lazy">
          <h3>{{ section.settings.section_1_image_2_title }}</h3>
          <p>{{ section.settings.section_1_image_2_text }}</p>
        </div>
        <div class="article-content__image-block">
          <img src="{{ section.settings.section_1_image_3 | image_url: width: 800 }}" alt="" loading="lazy">
          <h3>{{ section.settings.section_1_image_3_title }}</h3>
          <p>{{ section.settings.section_1_image_3_text }}</p>
        </div>
      </div>
    </div>
    <div class="article-content__section" id="{{ section.settings.section_2_id }}">
      <h2 class="article-content__heading">{{ section.settings.section_2_heading }}</h2>
      <p class="article-content__paragraph">{{ section.settings.section_2_description }}</p>
      <div class="article-content__symptoms">
        <div class="article-content__symptom">
          <h4>{{ section.settings.section_2_feature_1_title }}</h4>
          <p>{{ section.settings.section_2_feature_1_text }}</p>
        </div>
        <div class="article-content__symptom">
          <h4>{{ section.settings.section_2_feature_2_title }}</h4>
          <p>{{ section.settings.section_2_feature_2_text }}</p>
        </div>
        <div class="article-content__symptom">
          <h4>{{ section.settings.section_2_feature_3_title }}</h4>
          <p>{{ section.settings.section_2_feature_3_text }}</p>
        </div>
        <div class="article-content__symptom">
          <h4>{{ section.settings.section_2_feature_4_title }}</h4>
          <p>{{ section.settings.section_2_feature_4_text }}</p>
        </div>
      </div>
    </div>
    <div class="article-content__section" id="{{ section.settings.section_3_id }}">
      <h2 class="article-content__heading">{{ section.settings.section_3_heading }}</h2>
      <p class="article-content__paragraph">{{ section.settings.section_3_description }}</p>
      <div class="article-content__prevent-grid">
        <div class="article-content__hardware-image">
          {% for block in section.blocks %}
            <div class="tab-image" data-tab-id="{{ block.id }}">
              <img src="{{ block.settings.tab_image | image_url: width: 800 }}" alt="" loading="lazy">
            </div>
          {% endfor %}
        </div>
        <div class="article-content__tips">
          {% for block in section.blocks %}
            <details {{ block.shopify_attributes }} data-tab-trigger="{{ block.id }}">
              <summary>{{ block.settings.tab_title }}</summary>
              <p>{{ block.settings.tab_text }}</p>
            </details>
          {% endfor %}
        </div>
      </div>
    </div>
    <div class="article-content__section" id="{{ section.settings.section_3_subtitle_1_id }}">
      <h2 class="article-content__heading">{{ section.settings.section_3_subtitle_1 }}</h2>
      <p class="article-content__paragraph">{{ section.settings.section_3_text_1 }}</p>
    </div>

    <div class="article-content__section" id="{{ section.settings.section_3_subtitle_2_id }}">
      <h2 class="article-content__heading">{{ section.settings.section_3_subtitle_2 }}</h2>
      <p class="article-content__paragraph">{{ section.settings.section_3_text_2 }}</p>
    </div>
  </div>

  {% style %}
  .custom-blog {
    margin: unset !important;
    max-width: unset !important;
    padding: 0 !important;
    width: 100% !important;
}
    .article-content {
      background-color: #ffffff;
      padding: 60px 16px;
    }
    .article-content__wrapper {
      max-width: 1608px;
      margin: 0 auto;
    }
    .article-content__nav ul {
      display: flex;
      flex-wrap: wrap;
      gap: 16px;
      margin-bottom: 32px;
      padding-left: 0;
      list-style: none;
    }
    .article-content__nav li a {
      text-decoration: none;
      color: #2e2e2e;
      font-weight: 500;
    }
    .article-content__nav li a:hover {
      text-decoration: underline;
    }
    .article-content__heading {
      color: #1d1d1b;
      font-size: 32px;
      margin-bottom: 16px;
    }
    .article-content__paragraph {
      font-size: 18px;
      color: #2e2e2e;
      margin-bottom: 24px;
    }
    .article-content__image-grid {
      display: flex;
      gap: 24px;
      flex-wrap: wrap;
    }
    .article-content__image-block {
      flex: 1 1 calc(33.333% - 16px);
      min-width: 300px;
    }
    .article-content__image-block img {
      width: 100%;
      height: auto;
      margin-bottom: 8px;
    }
    .article-content__symptoms {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 24px;
    }
    .article-content__prevent-grid {
      display: flex;
      flex-wrap: wrap;
      gap: 40px;
    }
    .article-content__hardware-image {
      flex: 1 1 40%;
    }
    .article-content__hardware-image img {
      width: 100%;
      height: auto;
    }
    .article-content__tips {
      flex: 1 1 50%;
    }
    .article-content__tips details {
      margin-bottom: 16px;
    }
    .article-content__tips summary {
      font-weight: 600;
      cursor: pointer;
    }
    .tab-image {
      display: none;
    }
  {% endstyle %}

  <script>
    document.addEventListener('DOMContentLoaded', function () {
      const triggers = document.querySelectorAll('[data-tab-trigger]');
      const images = document.querySelectorAll('.tab-image');
      triggers.forEach(trigger => {
        trigger.addEventListener('toggle', function () {
          if (this.open) {
            images.forEach(img => img.style.display = 'none');
            const target = document.querySelector(`.tab-image[data-tab-id='${this.dataset.tabTrigger}']`);
            if (target) target.style.display = 'block';
          }
        });
      });
    });
  </script>
</section>
