{% schema %}
{
  "name": "Box Section",
  "tag": "section",
  "class": "box-section",
  "settings": [
    {
      "type": "text",
      "id": "main_title",
      "label": "Main Title",
      "default": "Effortless Entertainment, Rain or Shine."
    },
    {
      "type": "textarea",
      "id": "description",
      "label": "Description",
      "default": "Add a short description about this section."
    },
    {
      "type": "url",
      "id": "button_link",
      "label": "Button Link"
    },
    {
      "type": "text",
      "id": "button_text",
      "label": "Button Text",
      "default": "Learn More"
    },
    {"type": "range", "id": "padding_top", "label": "Padding top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "padding_bottom", "label": "Padding bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "padding_left", "label": "Padding left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "padding_right", "label": "Padding right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "margin_top", "label": "Margin top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "margin_bottom", "label": "Margin bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "margin_left", "label": "Margin left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"},
    {"type": "range", "id": "margin_right", "label": "Margin right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px"}
  ],
  "blocks": [
    {
      "type": "feature_box",
      "name": "Feature Box",
      "settings": [
        {
          "type": "image_picker",
          "id": "icon",
          "label": "Icon Image"
        },
        {
          "type": "text",
          "id": "title",
          "label": "Title",
          "default": "Box Title"
        },
        {
          "type": "textarea",
          "id": "text",
          "label": "Text",
          "default": "Box description text goes here."
        }
      ]
    }
  ],
  "max_blocks": 6,
  "presets": [
    {
      "name": "Box Section",
      "category": "Somos Custom",
      "blocks": [
        {
          "type": "feature_box"
        },
        {
          "type": "feature_box"
        },
        {
          "type": "feature_box"
        },
        {
          "type": "feature_box"
        }
      ]
    }
  ]
}
{% endschema %}

<div class="box-section-wrapper padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px; margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px">
  <div class="container">
    {% if section.settings.main_title != blank %}
      <h2 class="box-section-title">{{ section.settings.main_title }}</h2>
    {% endif %}
    {% if section.settings.description != blank %}
      <p class="box-section-description">{{ section.settings.description }}</p>
    {% endif %}

    <div class="box-grid">
      {% for block in section.blocks %}
        <div class="box-item" {{ block.shopify_attributes }}>
          {% if block.settings.icon != blank %}
            <img src="{{ block.settings.icon | img_url: '100x' }}" alt="{{ block.settings.title }}" class="box-icon">
          {% endif %}
          <h3 class="box-title">{{ block.settings.title }}</h3>
          <p class="box-text">{{ block.settings.text }}</p>
        </div>
      {% endfor %}
    </div>

    {% if section.settings.button_text != blank and section.settings.button_link != blank %}
      <div class="box-button-wrapper">
        <a href="{{ section.settings.button_link }}" class="box-button">{{ section.settings.button_text }}</a>
      </div>
    {% endif %}
  </div>
</div>

<style>
.box-section-wrapper {
  padding: 60px 20px;
  background-color: #fff;
  text-align: center;
}

.box-section-title {
  font-size: 2rem;
  margin-bottom: 10px;
}

.box-section-description {
  font-size: 1rem;
  color: #666;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.box-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 30px;
}

.box-item {
  max-width: 300px;
  margin: 0 auto;
}

.box-icon {
  display: block;
  height: 50px;
  margin: 0 auto 15px auto;
}

.box-title {
  font-size: 1.1rem;
  margin-bottom: 10px;
  color: #000;
}

.box-text {
  font-size: 0.9rem;
  color: #666;
}

.box-button-wrapper {
  margin-top: 40px;
}

.box-button {
  display: inline-block;
  padding: 12px 24px;
  background-color: #000;
  color: #fff;
  text-decoration: none;
  border-radius: 4px;
}
</style>
