 <script>
+function initCustomControls(flickity, container) {
+  const tabContent = container.closest('.tab-content');
+
+  // Bind arrows
+  const prevBtn = tabContent.querySelector('.slider-arrow.prev');
+  const nextBtn = tabContent.querySelector('.slider-arrow.next');
+  if (prevBtn && nextBtn) {
+    prevBtn.onclick = () => flickity.previous();
+    nextBtn.onclick = () => flickity.next();
+  }
+
+  // Build dots
+  const dotsWrapper = tabContent.querySelector('.slider-dots');
+  if (dotsWrapper) {
+    dotsWrapper.innerHTML = '';
+
+    flickity.slides.forEach((_, i) => {
+      const dot = document.createElement('span');
+      dot.className = 'slider-dot';
+      if (i === flickity.selectedIndex) dot.classList.add('active');
+
+      dot.addEventListener('click', () => flickity.select(i));
+      dotsWrapper.appendChild(dot);
+    });
+
+    // Update dot on slide change
+    flickity.on('select', () => {
