{{ 'https://cdnjs.cloudflare.com/ajax/libs/flickity/2.3.0/flickity.min.css' | stylesheet_tag }}
{{ 'https://cdnjs.cloudflare.com/ajax/libs/flickity/2.3.0/flickity.pkgd.min.js' | script_tag }}

<style>
  .bundles-slider {
    position: relative;
    overflow: hidden;
  }

  .bundles-slider .tabs {
    display: flex;
    justify-content: center;
    gap: 16px;
    background: #FAFAFA;
    padding: 12px 20px;
    border-radius: 9999px;
    margin: 0 auto 24px;
    width: fit-content;
    flex-wrap: wrap;
  }

  .bundles-slider .tab {
    padding: 8px 16px;
    border-radius: 9999px;
    cursor: pointer;
    transition: background 0.3s;
  }

  .bundles-slider .tab.active {
    background: #20413B;
    color: white;
  }

  .bundles-slider .bundle-slider {
    width: 100%;
  }

  .bundles-slider .bundle-card {
    background: white;
    border-radius: 12px;
    padding: 16px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.05);
    position: relative;
    text-align: center;
  }

  .bundles-slider .bundle-card .savings {
    position: absolute;
    top: 0;
    right: 0;
    background: #DB4B97;
    color: white;
    padding: 4px 10px;
    font-size: 14px;
    border-bottom-left-radius: 8px;
  }

  .bundles-slider .flickity-prev-next-button {
    background: white;
    border-radius: 9999px;
    border: 1px solid #ccc;
    width: 34px;
    height: 34px;
  }

  .bundles-slider .flickity-page-dots {
    bottom: -30px;
    left: 0;
    display: flex;
    gap: 8px;
    justify-content: flex-start;
    padding-left: 16px;
  }

  .bundles-slider .flickity-page-dots .dot {
    width: 24px;
    height: 4px;
    border-radius: 2px;
    background: #ccc;
    opacity: 1;
    transition: background 0.3s;
  }

  .bundles-slider .flickity-page-dots .dot.is-selected {
    background: #DB4B97;
  }

  .section_width_standard,
  .section_width_full {
    transition: all 0.3s ease;
  }

  .section_width_full {
    width: 100%;
  }

  .section_width_standard {
    margin: 0 auto !important;
    width: 90% !important;
  }

  .section_content_width_standard,
  .section_content_width_full {
    transition: all 0.3s ease;
  }

  .section_content_width_full {
    width: 100%;
  }

  .section_content_width_standard {
    margin: 0 auto !important;
    width: 90% !important;
  }
</style>

<section
  id="{{ section.settings.custom_id }}"
  class="bundles-slider section_width_{{ section.settings.section_width }} {{ section.settings.custom_class }}"
  style="
    background: {{ section.settings.background_color }};
    {% if section.settings.background_image != blank %}background-image: url({{ section.settings.background_image | image_url }}); background-size: cover; background-position: center;{% endif %}
    padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px;
    margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;
    height: {{ section.settings.height_desktop }}px;
  "
>
  <div class="section_content_width_{{ section.settings.section_content_width }}">
    {% if section.settings.section_title != blank %}
      <{{ section.settings.section_title_tag }} style="text-align:{{ section.settings.section_title_align }}; color:{{ section.settings.section_title_color }}; font-weight:{{ section.settings.section_title_weight }};">
        {{ section.settings.section_title }}
      </{{ section.settings.section_title_tag }}>
    {% endif %}

    {% if section.settings.section_text != blank %}
      <div style="text-align:{{ section.settings.section_text_align }};">
        {{ section.settings.section_text }}
      </div>
    {% endif %}

    <div class="tabs">
      {% for block in section.blocks %}
        <div class="tab {% if forloop.first %}active{% endif %}" data-tab="tab-{{ block.id }}">
          {{ block.settings.tab_label }}
        </div>
      {% endfor %}
    </div>

    {% for block in section.blocks %}
      <div class="tab-content tab-tab-{{ block.id }} {% if forloop.first %}active{% endif %}">
        <div class="bundle-slider js-flickity"
          data-flickity='{ "wrapAround": true, "cellAlign": "left", "pageDots": {{ section.settings.show_dots }}, "prevNextButtons": {{ section.settings.show_arrows }} }'>
          {% for bundle in block.settings.bundles %}
            <div class="bundle-card">
              <div class="savings">{{ bundle.save_label }}</div>
              {% if bundle.image %}
                <img src="{{ bundle.image | image_url: width: 600 }}" loading="{{ bundle.lazy_loading | default: true | json }}">
              {% endif %}
              <h4>{{ bundle.title }}</h4>
              <p>{{ bundle.text }}</p>
            </div>
          {% endfor %}
        </div>
      </div>
    {% endfor %}
  </div>
</section>

<script>
  document.addEventListener('DOMContentLoaded', function () {
    document.querySelectorAll('.bundles-slider .tab').forEach(tab => {
      tab.addEventListener('click', function () {
        document.querySelectorAll('.bundles-slider .tab').forEach(t => t.classList.remove('active'));
        tab.classList.add('active');

        const tabId = tab.dataset.tab;
        document.querySelectorAll('.bundles-slider .tab-content').forEach(c => c.classList.remove('active'));
        document.querySelector('.bundles-slider .'+tabId).classList.add('active');
      });
    });
  });
</script>

