+<script>
+document.addEventListener('DOMContentLoaded', function () {
+  const section = document.querySelector('.bundle-tabs-section');
+  const tabs = section.querySelectorAll('.tab');
+  const contents = section.querySelectorAll('.tab-content');
+
+  function activateTab(tabId) {
+    contents.forEach(c => c.style.display = 'none');
+    tabs.forEach(t => t.classList.remove('active'));
+
+    const target = section.querySelector(`#${tabId}`);
+    const tab = section.querySelector(`[data-tab-id="${tabId}"]`);
+    if (target && tab) {
+      target.style.display = 'block';
+      tab.classList.add('active');
+    }
+  }
+
+  tabs.forEach(tab => {
+    tab.addEventListener('click', () => activateTab(tab.dataset.tabId));
+  });
+
+  if (tabs.length > 0) {
+    activateTab(tabs[0].dataset.tabId);
+  }
+});
