+{% style %}
+.product-comparison-section {
+  padding: 40px 20px;
+}
+.comparison-container {
+  max-width: 1608px;
+  margin: 0 auto;
+}
+.comparison-header h2 {
+  font-family: 'Questrial', sans-serif;
+  font-size: 48px;
+  margin-bottom: 6px;
+  color: #1a1a1a;
+  text-align: left;
+}
+.comparison-header p {
+  font-family: 'Roboto', sans-serif;
+  font-size: 18px;
+  color: #1a1a1a;
+  margin-bottom: 36px;
+  text-align: left;
+}
+.comparison-products {
+  display: flex;
+  justify-content: center;
+  gap: 48px;
+  margin-bottom: 40px;
+}
+.comparison-product {
+  flex: 0 0 300px;
+  text-align: left;
+}
+.product-image {
+  max-height: 180px;
+  margin-bottom: 12px;
+  margin-left: auto;
+  margin-right: auto;
+  display: block;
+}
+.image-placeholder {
+  height: 180px;
+  background: #f4f4f4;
+  margin-bottom: 12px;
+}
+.product-title {
+  font-family: 'Questrial', sans-serif;
+  font-size: 18px;
+  color: #1a1a1a;
+  margin-bottom: 6px;
+}
+.product-price {
+  font-family: 'Roboto', sans-serif;
+  font-size: 16px;
+  color: rgba(26, 26, 26, 0.7);
+  margin-bottom: 12px;
+}
+.view-button {
+  background: linear-gradient(90deg, #57a653, #1ba613);
+  color: #fff;
+  padding: 12px 20px;
+  font-size: 14px;
+  font-family: 'Roboto', sans-serif;
+  border-radius: 30px;
+  text-decoration: none;
+  display: inline-flex;
+  align-items: center;
+  gap: 8px;
+  justify-content: center;
+  border: none;
+  cursor: pointer;
+}
+.view-button svg {
+  flex-shrink: 0;
+}
+.comparison-table {
+  border-top: 1px solid rgba(26, 26, 26, 0.12);
+}
+.comparison-row {
+  display: grid;
+  grid-template-columns: 300px repeat(3, 300px);
+  align-items: center;
+  height: 74.6px;
+  padding: 0 0 0 0;
+  border-top: 1px solid rgba(26, 26, 26, 0.12);
+  gap: 48px;
+}
+.comparison-row.last-row {
+  border-bottom: none;
+}
+.feature-name {
+  font-family: 'Barlow', 'Roboto', sans-serif;
+  font-weight: 700;
+  font-size: 16px;
+  color: #1a1a1a;
+  padding-left: 16px;
+}
+.feature-value {
+  font-family: 'Roboto', sans-serif;
+  font-size: 16px;
+  color: rgba(26, 26, 26, 0.7);
+  text-align: left;
+  display: flex;
+  justify-content: flex-start;
+  align-items: center;
+}
+.comparison-row.last-row .feature-value svg {
+  margin: 0 auto;
+}
+.view-all-wrapper {
+  text-align: center;
+  margin-top: 24px;
+}
+.view-all-button {
+  font-family: 'Basic Sans', sans-serif;
+  font-size: 13px;
+  color: #000;
+  background: none;
+  border: none;
+  cursor: pointer;
+  display: inline-flex;
+  align-items: center;
+  gap: 6px;
+  padding: 0;
+}
+.view-all-button svg {
+  flex-shrink: 0;
+}
+@media screen and (max-width: 768px) {
+  .comparison-products {
+    flex-direction: column;
+    gap: 32px;
   }
+  .comparison-row {
+    grid-template-columns: 1fr;
+    text-align: left;
+    height: auto;
+    border-top: 1px solid #d9d9d9;
+  }
+  .feature-name {
+    padding-left: 0;
+  }
+  .feature-value {
+    justify-content: flex-start;
+    padding-left: 16px;
+  }
+}
+{% endstyle %}

