  color: #ffffff;
  background: #1a1a1a;
  padding: 6px 12px;
  text-transform: capitalize;
  letter-spacing: 0.2px;
  border-radius: 0;
  z-index: 2;
}
.custom-blog__card-title {
  font-size: 24px;
  font-family: 'Questrial', sans-serif;
  font-weight: 400;
  line-height: 1.4;
  color: #1D1D1B;
}
.custom-blog__card-title a {
  text-decoration: none;
  color: #1a1a1a;
}
.custom-blog__card-excerpt-arrow {
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  gap: 12px;
}
.custom-blog__card-excerpt {
  font-size: 16px;
  font-family: 'Roboto', sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: #636363;
  max-height: none;
  overflow: visible;
  margin: 0;
}
.custom-blog__card-arrow {
  position: static;
  display: inline-flex;
  justify-content: center;
  align-items: center;
  padding: 0;
  border-radius: 2px;
}
.custom-blog__pagination {
  text-align: center;
  margin-bottom: 80px;
}
.custom-blog__pagination a,
.custom-blog__pagination span {
  margin: 0 4px;
  padding: 8px 12px;
  font-size: 14px;
  text-decoration: none;
  color: #1a1a1a;
  border-radius: 4px;
}
.custom-blog__pagination .current {
  font-weight: bold;
}

@media screen and (max-width: 767px) {
  .custom-blog__banner {
    height: 600px;
    background-position: center;
  }
  .custom-blog__banner-inner {
    top: 354px;
    left: 24px;
    gap: 22px;
    max-width: 282px;
  }
  .custom-blog__label {
    font-size: 14px;
  }
  .custom-blog__subtitle {
    font-size: 14px;
  }
  .custom-blog__heading {
    font-size: 24px;
    max-width: 100%;
    line-height: 1.3;
  }
  .custom-blog__grid {
    grid-template-columns: repeat(1, 1fr);
    margin: 40px 24px;
  }
}
{% endstyle %}
