+{{ 'https://cdnjs.cloudflare.com/ajax/libs/flickity/2.3.0/flickity.min.css' | stylesheet_tag }}
+{{ 'https://cdnjs.cloudflare.com/ajax/libs/flickity/2.3.0/flickity.pkgd.min.js' | script_tag }}
+
+
+{% comment %}
+  Custom section: Custom Blog | custom-blog.liquid
+{% endcomment %}
+<section class="custom-blog" style="background: {{ section.settings.bg_color }};">
+
+  {%- assign featured_article = blog.articles.first -%}
+  {%- assign override_img = section.settings.custom_banner_override_image -%}
+
+  <!-- Banner -->
+  <div class="custom-blog__banner" style="background-image: url('{{ override_img | default: featured_article.image | image_url: width: 2000 }}');">
+    <div class="custom-blog__banner-inner">
+      <div class="custom-blog__label">
+        {{ section.settings.banner_label | default: featured_article.tags.first }}
+      </div>
+      <div class="custom-blog__subtitle">
+        {{ section.settings.banner_subtitle | default: featured_article.author }}
+      </div>
+      <h1 class="custom-blog__heading">
+        {{ section.settings.custom_banner_title | default: featured_article.title }}
+      </h1>
+      <a href="{{ featured_article.url }}" class="custom-blog__arrow">
+        {% render 'diagonal-bottom-left-arrow.svg' %}
+      </a>
+    </div>
+  </div>
+
+  <!-- Slider -->
+<section id="blog-slider-{{ section.id }}"
+  class="blog-slider section_width_{{ section.settings.section_width }}"
+  style="
+    background-color: {{ section.settings.background_color }};
+    padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px;
+    margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;
+  "
+>
+  <div class="slider-text section_content_width_{{ section.settings.section_content_width }} {{ section.settings.text_alignment }}">
+    <div class="slider-heading-small">{{ section.settings.heading }}</div>
+    <h2 class="slider-heading-main">{{ section.settings.subheading }}</h2>
+    <div class="slider-description">{{ section.settings.description }}</div>
+  </div>
+
+  <div class="slider-carousel-wrapper">
+    <button class="slider-nav prev"></button>
+
+    <div class="slider-carousel">
+      <div class="slider-track">
+        {% assign blog_handle = section.settings.blog | default: 'news' %}
+        {% assign blog_posts = blogs[blog_handle].articles | slice: 0, 10 %}
+        {% for article in blog_posts %}
+          <div class="slider-slide">
+            <div class="blog-post-card">
+              {% if article.image %}
+                <img src="{{ article.image | image_url: width: 800 }}" alt="{{ article.title }}">
+              {% endif %}
+              <div class="blog-post-content">
+                <div class="blog-post-tags">
+                  {% for tag in article.tags limit: 2 %}
+                    <span class="tag">{{ tag }}</span>
+                  {% endfor %}
+                </div>
+                <div class="blog-post-date">{{ article.published_at | date: "%B %d, %Y" }}</div>
+                <div class="blog-post-title">{{ article.title }}</div>
+                <div class="blog-post-arrow">
+                  <a href="{{ article.url }}"></a>
+                </div>
+              </div>
+            </div>
+          </div>
+        {% endfor %}
+      </div>
+    </div>
+
+    <button class="slider-nav next"></button>
+  </div>
+
+  <div class="slider-controls section_content_width_{{ section.settings.section_content_width }}" style="display: flex; justify-content: space-between; align-items: center; margin-top: 3rem;">
+    <div class="slider-dots">
+      {% for article in blogs[section.settings.blog].articles limit: 10 %}
+        <span class="slider-dot{% if forloop.first %} active{% endif %}" data-index="{{ forloop.index0 }}"></span>
+      {% endfor %}
+    </div>
+    {% if section.settings.show_arrows %}
+      <div class="slider-arrows">
+        <button class="slider-arrow prev"></button>
+        <button class="slider-arrow next"></button>
+      </div>
+    {% endif %}
+  </div>
+</section>
+
+<script>
+  document.addEventListener('DOMContentLoaded', () => {
+    const section = document.querySelector('#blog-slider-{{ section.id }}');
+    const container = section.querySelector('.slider-carousel');
+    const track = section.querySelector('.slider-track');
+    const slides = section.querySelectorAll('.slider-slide');
+    const dots = section.querySelectorAll('.slider-dot');
+
+    const navPrev = section.querySelector('.slider-nav.prev');
+    const navNext = section.querySelector('.slider-nav.next');
+    const ctrlPrev = section.querySelector('.slider-arrow.prev');
+    const ctrlNext = section.querySelector('.slider-arrow.next');
+
+    const slideWidth = slides[0]?.offsetWidth + 24 || 384;
+    let currentIndex = 0;
+
+    function goTo(index) {
+      index = Math.max(0, Math.min(index, slides.length - 1));
+      container.scrollTo({ left: slides[index].offsetLeft - 12, behavior: 'smooth' });
+      dots.forEach(dot => dot.classList.remove('active'));
+      dots[index]?.classList.add('active');
+      currentIndex = index;
+    }
+
+    navPrev?.addEventListener('click', () => goTo(currentIndex - 1));
+    navNext?.addEventListener('click', () => goTo(currentIndex + 1));
+    ctrlPrev?.addEventListener('click', () => goTo(currentIndex - 1));
+    ctrlNext?.addEventListener('click', () => goTo(currentIndex + 1));
+
+    dots.forEach((dot, i) => {
+      dot.addEventListener('click', () => goTo(i));
+    });
+
+    goTo(0);
+  });
+</script>
+
+  <!-- Blog List Title -->
+  <h2 class="custom-blog__list-title" style="text-align:center; margin-top: 40px;">
+    {{ section.settings.blog_list_title }}
+  </h2>
+
+  <!-- Filter/Sort Row -->
+  <div class="custom-blog__filter-sort">
+    <button class="custom-blog__filter-btn">
+      <span class="icon">
+        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="none" viewBox="0 0 16 16">
+          <path d="M0 5H16" stroke="#6C707A"/>
+          <path d="M0 11H16" stroke="#6C707A"/>
+          <path d="M14 3H12V7H14V3Z" fill="#6C707A"/>
+          <path d="M5 9H3V13H5V9Z" fill="#6C707A"/>
+        </svg>
+      </span>
+      Filter
+    </button>
+    <button class="custom-blog__sort-btn">
+      <span class="icon">
+        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="none" viewBox="0 0 16 16">
+          <path d="M3 6L8 11L13 6" stroke="#6C707A"/>
+        </svg>
+      </span>
+      Sort
+    </button>
+  </div>
+
+  {% paginate blog.articles by 9 %}
+    <!-- Blog Grid -->
+    <div class="custom-blog__grid">
+      {% for article in blog.articles %}
+        <div class="custom-blog__card" data-category="{{ article.tags | join: ' ' }}">
+          <div class="custom-blog__card-image">
+            {% if article.image %}
+              <a href="{{ article.url }}">
+                <img
+                  src="{{ article.image | image_url: width: 600 }}"
+                  alt="{{ article.title }}"
+                  style="aspect-ratio: 520 / 288; max-width: 519.98px; max-height: 287.98px; width: 100%; height: auto; object-fit: cover;">
+              </a>
+            {% else %}
+              <div class="custom-blog__image-placeholder"></div>
+            {% endif %}
+            {% if article.tags.size > 0 %}
+              <div class="custom-blog__card-label">{{ article.tags.first }}</div>
+            {% endif %}
+          </div>
+
+          <div class="custom-blog__card-content">
+            <h3 class="custom-blog__card-title">
+              <a href="{{ article.url }}">{{ article.title }}</a>
+            </h3>
+            <div class="custom-blog__card-excerpt-arrow">
+              <p class="custom-blog__card-excerpt">
+                {{ article.excerpt_or_content | strip_html | truncate: 140 }}
+              </p>
+              <a class="custom-blog__card-arrow" href="{{ article.url }}">
+                {% render 'diagonal-bottom-left-arrow.svg' %}
+              </a>
+            </div>
+          </div>
+        </div>
+      {% endfor %}
+    </div>
+
+    <!-- Pagination -->
+    <div class="custom-blog__pagination">
+      {% if paginate.previous %}
+        <a href="{{ paginate.previous.url }}">&lt;</a>
+      {% endif %}
+      {% for part in paginate.parts %}
+        {% if part.is_link %}
+          <a href="{{ part.url }}">{{ part.title }}</a>
+        {% else %}
+          <span class="current">{{ part.title }}</span>
+        {% endif %}
+      {% endfor %}
+      {% if paginate.next %}
+        <a href="{{ paginate.next.url }}">&gt;</a>
+      {% endif %}
+    </div>
+  {% endpaginate %}
+</section>
+
+
+
+{% schema %}
+{
+  "name": "Custom Blog",
+  "tag": "section",
+  "class": "custom-blog",
+  "settings": [
+    {
+      "type": "image_picker",
+      "id": "banner_image",
+      "label": "Banner Background Image"
+    },
+    {
+      "type": "image_picker",
+      "id": "custom_banner_override_image",
+      "label": "Override Banner Image (optional)"
+    },
+    {
+      "type": "text",
+      "id": "custom_banner_title",
+      "label": "Custom Banner Title (optional)"
+    },
+    {
+      "type": "text",
+      "id": "banner_label",
+      "label": "Banner Label",
+      "default": "Education"
+    },
+    {
+      "type": "text",
+      "id": "banner_subtitle",
+      "label": "Banner Subtitle",
+      "default": "Education & Industry"
+    },
+    {
+      "type": "url",
+      "id": "banner_link",
+      "label": "Banner Arrow Link",
+      "default": "/"
+    },
+    {
+      "type": "text",
+      "id": "blog_list_title",
+      "label": "Blog List Section Title",
+      "default": "More Articles"
+    },
+    {
+      "type": "color",
+      "id": "bg_color",
+      "label": "Background Color",
+      "default": "#ffffff"
+    },
+    {
+      "type": "select",
+      "id": "section_width",
+      "label": "Section Width",
+      "default": "standard",
+      "options": [
+        { "value": "standard", "label": "Standard Width" },
+        { "value": "full", "label": "Full Width" }
+      ]
+    },
+    {
+      "type": "select",
+      "id": "section_content_width",
+      "label": "Content Width",
+      "default": "standard",
+      "options": [
+        { "value": "standard", "label": "Standard Width" },
+        { "value": "full", "label": "Full Width" }
+      ]
+    },
+    {
+      "type": "color",
+      "id": "background_color",
+      "label": "Background Color",
+      "default": "#ffffff"
+    },
+    { "type": "range", "id": "padding_top", "label": "Padding Top", "min": 0, "max": 100, "step": 5, "default": 40 },
+    { "type": "range", "id": "padding_bottom", "label": "Padding Bottom", "min": 0, "max": 100, "step": 5, "default": 40 },
+    { "type": "range", "id": "padding_left", "label": "Padding Left", "min": 0, "max": 100, "step": 5, "default": 20 },
+    { "type": "range", "id": "padding_right", "label": "Padding Right", "min": 0, "max": 100, "step": 5, "default": 20 },
+    { "type": "range", "id": "margin_top", "label": "Margin Top", "min": 0, "max": 100, "step": 5, "default": 0 },
+    { "type": "range", "id": "margin_bottom", "label": "Margin Bottom", "min": 0, "max": 100, "step": 5, "default": 0 },
+    { "type": "range", "id": "margin_left", "label": "Margin Left", "min": 0, "max": 100, "step": 5, "default": 0 },
+    { "type": "range", "id": "margin_right", "label": "Margin Right", "min": 0, "max": 100, "step": 5, "default": 0 },
+    {
+      "type": "select",
+      "id": "text_alignment",
+      "label": "Text Alignment",
+      "options": [
+        { "value": "left", "label": "Left" },
+        { "value": "center", "label": "Center" },
+        { "value": "right", "label": "Right" }
+      ],
+      "default": "left"
+    },
+    {
+      "type": "text",
+      "id": "heading",
+      "label": "Small Heading",
+      "default": "Latest News"
