+{% style %}
+ .slider-carousel-wrapper {
+    position: relative;
+    display: flex;
+    align-items: center;
+    gap: 12px;
+  }
+
+  .slider-carousel {
+    overflow-x: auto;
+    scroll-behavior: smooth;
+    flex: 1;
+  }
+
+  .slider-track {
+    display: flex;
+    gap: 24px;
+    scroll-snap-type: x mandatory;
+    scroll-behavior: smooth;
+    padding-bottom: 2rem;
+  }
+
+  .slider-slide {
+    scroll-snap-align: center;
+    flex: 0 0 auto;
+    width: {{ section.settings.slide_width }}px;
+  }
+
+  .slider-nav,
+  .slider-arrow {
+    background: white;
+    border: 1px solid #ccc;
+    width: 36px;
+    height: 36px;
+    font-size: 20px;
+    border-radius: 50%;
+    cursor: pointer;
+  }
+
+  .slider-dots {
+    display: flex;
+    gap: 8px;
+  }
+
+  .slider-dot {
+    width: 24px;
+    height: 25px;
+    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='25'><rect x='4' y='11.95' width='16' height='2' rx='1' fill='%23636363'/></svg>") no-repeat center center;
+    background-size: contain;
+    cursor: pointer;
+  }
+
+  .slider-dot.active {
+    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='25'><rect x='4' y='11.95' width='16' height='2' rx='1' fill='%23DB4B97'/></svg>");
+  }
+
+  .blog-post-card {
+    background: #fff;
+    border-radius: 12px;
+    overflow: hidden;
+    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.06);
+    display: flex;
+    flex-direction: column;
+    height: 100%;
+  }
+
+  .blog-post-card img {
+    width: 100%;
+    height: 200px;
+    object-fit: cover;
+  }
+
+  .blog-post-content {
+    padding: 20px;
+    display: flex;
+    flex-direction: column;
+    gap: 8px;
+    flex-grow: 1;
+  }
+
+  .blog-post-tags {
+    display: flex;
+    gap: 6px;
+    flex-wrap: wrap;
+  }
+
+  .blog-post-tags .tag {
+    background: #20413B;
+    color: white;
+    font-size: 12px;
+    padding: 2px 10px;
+    border-radius: 6px;
+  }
+
+  .blog-post-date {
+    font-size: 12px;
+    color: #888;
+  }
+
+  .blog-post-title {
+    font-size: 16px;
+    font-weight: 600;
+    color: #111;
+    line-height: 1.4;
+  }
+
+  .blog-post-arrow {
+    margin-top: auto;
+  }
+
+  .blog-post-arrow a {
+    display: inline-block;
+    padding: 6px 12px;
+    border: 1px solid #333;
+    border-radius: 6px;
+    font-size: 18px;
+    line-height: 1;
+    text-decoration: none;
+  }
+  
+
+/* Add this to your existing <style> block or merge it */
+.custom-blog__slider {
+  max-width: 1600px;
+  margin: 40px auto;
+}
+.custom-blog__slider-item {
+  width: 300px;
+  margin-right: 20px;
+}
+.custom-blog__slider-item img {
+  width: 100%;
+  height: auto;
+  border-radius: 4px;
+  object-fit: cover;
+}
+.custom-blog__slider-caption {
+  padding-top: 10px;
+}
+.custom-blog__slider-date {
+  font-size: 12px;
+  color: #777;
+}
+.custom-blog__slider-title {
+  font-size: 16px;
+  font-weight: 500;
+  color: #1D1D1B;
+}
+/* New CSS */
+.custom-blog__banner {
+  height: 600px;
+  overflow: hidden;
+  background-size: cover;
+  background-position: center;
+  color: #ffffff;
+}
+.custom-blog__banner-inner {
+  position: absolute;
+  left: 157px;
+  top: 154.5px;
+  gap: 20px;
+  max-width: 688px;
+  display: flex;
+  flex-direction: column;
+}
+.custom-blog__label {
+  padding: 6px 10px;
+  background: #1D1D1B;
+  font-size: 16px;
+  font-weight: 400;
+  font-family: Roboto, sans-serif;
+  color: white;
+  display: inline-flex;
+  align-items: center;
+  width: max-content;
+}
+.custom-blog__subtitle {
+  font-size: 18px;
+  font-family: Questrial, sans-serif;
+  font-weight: 400;
+  color: white;
+}
+.custom-blog__heading {
+  font-size: 56px;
+  font-family: Questrial, sans-serif;
+  font-weight: 400;
+  color: white;
+  max-width: 657px;
+  line-height: 1.2;
+}
+.custom-blog__arrow {
+  display: inline-flex;
+  align-items: center;
+  justify-content: left;
+  border-radius: 4px;
+  width: auto;
+  height: auto;
+}
+ .custom-blog__arrow rect {
+  stroke: #ffffff;
+ }
+  .custom-blog__arrow path { 
+  fill: #ffffff;
+}
+.custom-blog__nav {
+  background-color: #1a1a1a;
+  margin-bottom: 40px;
+}
+.custom-blog__nav ul {
+  display: flex;
+  justify-content: center;
+  gap: 48px;
+  list-style: none;
+  margin: 0 auto;
+  padding: 0;
+  height: 60px;
+  align-items: center;
+  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
+}
+.custom-blog__nav a {
+  font-size: 14px;
+  color: #ffffff;
+  text-decoration: none;
+  font-weight: 400;
+  position: relative;
+  padding-bottom: 12px;
+}
+.custom-blog__nav a.active {
+  font-weight: 600;
+}
+.custom-blog__nav a.active::after {
+  content: "";
+  position: absolute;
+  left: 50%;
+  bottom: -1px;
+  transform: translateX(-50%);
+  height: 2px;
+  width: 24px;
+  background: #ffffff;
+}
+.custom-blog__filter-sort {
+  display: flex;
+  justify-content: left;
+  gap: 16px;
+  margin: 40px auto;
+  max-width: 1608px;
+}
+.custom-blog__filter-btn,
+.custom-blog__sort-btn {
+  display: inline-flex;
