+                  src="{{ article.image | image_url: width: 600 }}"
+                  alt="{{ article.title }}"
+                  style="aspect-ratio: 520 / 288; max-width: 519.98px; max-height: 287.98px; width: 100%; height: auto; object-fit: cover;">
+              </a>
