+      "type": "text",
+      "id": "subheading",
+      "label": "Main Heading",
+      "default": "From the Blog"
+    },
+    {
+      "type": "textarea",
+      "id": "description",
+      "label": "Section Description",
+      "default": "See what's new at Sylvox."
+    },
+    {
+      "type": "range",
+      "id": "slide_width",
+      "label": "Slide Width",
+      "min": 280,
+      "max": 1000,
+      "step": 20,
+      "default": 360
+    },
+    {
+      "type": "checkbox",
+      "id": "show_arrows",
+      "label": "Show Arrows",
