+{% style %}
+.slider-track {
+    display: flex;
+    overflow-x: auto;
+    gap: 24px;
+    scroll-snap-type: x mandatory;
+    scroll-behavior: smooth;
+    padding-bottom: 2rem;
+  }
+
+  .slider-slide {
+    flex: 0 0 auto;
+    scroll-snap-align: center;
+    width: {{ section.settings.slide_width }}px;
+  display: flex;
+  flex-direction: column;
+  height: 100%;
+}
+
+.blog-post-card {
+  height: 100%;
+  display: flex;
+  flex-direction: column;
+}
+
+  .slider-controls {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    margin-top: 3rem;
+  }
+
+  .slider-dots {
+    display: flex;
+    gap: 8px;
+  }
+
+  .slider-dot {
+    width: 24px;
+    height: 25px;
+    background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='25'><rect x='4' y='11.95' width='16' height='2' rx='1' fill='%23636363'/></svg>") no-repeat center center;
+    background-size: contain;
+    cursor: pointer;
+  }
+
+  .slider-dot.active {
+    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='24' height='25'><rect x='4' y='11.95' width='16' height='2' rx='1' fill='%23DB4B97'/></svg>");
+  }
+
+  .slider-arrows {
+    display: flex;
+    gap: 10px;
+  }
+
+  .slider-arrow {
+    width: 40px;
+    height: 40px;
+    border-radius: 50%;
+    border: 1px solid #000;
+    background: white;
+    cursor: pointer;
+    font-size: 1.5rem;
+  }
+
+.slider-track {
+  scrollbar-width: none; /* Firefox */
+  -ms-overflow-style: none; /* IE 10+ */
+}
+
+.slider-track::-webkit-scrollbar {
+  display: none; /* Chrome, Safari, Opera */
+}
+
+
+  .blog-post-card img {
+  width: 100%;
+  height: 200px; /* or whatever fixed height you want */
+  object-fit: cover;
+}
+
+
+/* Add this to your existing <style> block or merge it */
+.custom-blog__slider {
+  max-width: 1600px;
+  margin: 40px auto;
+}
+.custom-blog__slider-item {
+  width: 300px;
+  margin-right: 20px;
+}
+.custom-blog__slider-item img {
+  width: 100%;
+  height: auto;
+  border-radius: 4px;
+  object-fit: cover;
+}
+.custom-blog__slider-caption {
+  padding-top: 10px;
+}
+.custom-blog__slider-date {
+  font-size: 12px;
+  color: #777;
+}
+.custom-blog__slider-title {
+  font-size: 16px;
+  font-weight: 500;
+  color: #1D1D1B;
+}
+/* New CSS */
+.custom-blog__banner {
+  height: 600px;
+  overflow: hidden;
+  background-size: cover;
+  background-position: center;
+  color: #ffffff;
+}
+.custom-blog__banner-inner {
+  position: absolute;
+  left: 157px;
+  top: 154.5px;
+  gap: 20px;
+  max-width: 688px;
+  display: flex;
+  flex-direction: column;
+}
+.custom-blog__label {
+  padding: 6px 10px;
+  background: #1D1D1B;
+  font-size: 16px;
+  font-weight: 400;
+  font-family: Roboto, sans-serif;
+  color: white;
+  display: inline-flex;
+  align-items: center;
+  width: max-content;
+}
+.custom-blog__subtitle {
+  font-size: 18px;
+  font-family: Questrial, sans-serif;
+  font-weight: 400;
+  color: white;
+}
+.custom-blog__heading {
+  font-size: 56px;
+  font-family: Questrial, sans-serif;
+  font-weight: 400;
+  color: white;
+  max-width: 657px;
+  line-height: 1.2;
+}
+.custom-blog__arrow {
+  display: inline-flex;
+  align-items: center;
+  justify-content: left;
+  border-radius: 4px;
+  width: auto;
+  height: auto;
+}
+ .custom-blog__arrow rect {
+  stroke: #ffffff;
+ }
+  .custom-blog__arrow path { 
+  fill: #ffffff;
+}
+.custom-blog__nav {
+  background-color: #1a1a1a;
+  margin-bottom: 40px;
+}
+.custom-blog__nav ul {
+  display: flex;
+  justify-content: center;
+  gap: 48px;
+  list-style: none;
+  margin: 0 auto;
+  padding: 0;
+  height: 60px;
+  align-items: center;
+  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
+}
+.custom-blog__nav a {
+  font-size: 14px;
+  color: #ffffff;
+  text-decoration: none;
+  font-weight: 400;
+  position: relative;
+  padding-bottom: 12px;
+}
+.custom-blog__nav a.active {
+  font-weight: 600;
+}
+.custom-blog__nav a.active::after {
+  content: "";
+  position: absolute;
+  left: 50%;
+  bottom: -1px;
+  transform: translateX(-50%);
+  height: 2px;
+  width: 24px;
+  background: #ffffff;
+}
+.custom-blog__filter-sort {
+  display: flex;
+  justify-content: left;
+  gap: 16px;
+  margin: 40px auto;
+  max-width: 1608px;
+}
+.custom-blog__filter-btn,
+.custom-blog__sort-btn {
+  display: inline-flex;
+  align-items: center;
+  gap: 8px;
+  padding: 8px 20px;
+  font-size: 14px;
+  font-weight: 500;
+  background: #ffffff;
+  color: #1a1a1a;
+  border: 1px solid #bdbdbd;
+  border-radius: 9999px;
+  cursor: pointer;
+}
+.custom-blog__grid {
+  display: grid;
+  grid-template-rows: repeat(3, 1fr) !important;
+  gap: 48px 24px;
+  max-width: 1608px;
+  margin: 0 auto 60px;
+  padding: 0;
+}
+.custom-blog__card {
+background: #fff;
+    display: flex;
+    flex-direction: row !important;
+    border-radius: 4px;
+    overflow: hidden;
+    position: relative;
+    /* max-width: 519.98px; */
+    width: 100%;
+    display: flex
+;
+}
+.custom-blog__card-image {
+  position: relative;
+}
+.custom-blog__card-image img {
+  width: 100%;
+  height: auto;
+  max-width: 519.98px;
+  max-height: 287.98px;
+  aspect-ratio: 520 / 288;
+  object-fit: cover;
+  display: block;
+}
+.custom-blog__card-content {
+  background: #FAFAFA;
+  padding: 20px;
+  border-radius: 4px;
+  display: flex;
+  flex-direction: column;
+  align-items: flex-start;
+  justify-content: space-between;
+  gap: 10px;
+  height: 100%;
+}
+.custom-blog__card-image .custom-blog__card-label {
+  position: absolute;
+  bottom: 20px;
+  left: 20px;
+  font-size: 12px;
+  font-weight: 500;
+  color: #ffffff;
+  background: #1a1a1a;
+  padding: 6px 12px;
+  text-transform: capitalize;
+  letter-spacing: 0.2px;
+  border-radius: 0;
+  z-index: 2;
+}
+.custom-blog__card-title {
+  font-size: 24px;
+  font-family: 'Questrial', sans-serif;
+  font-weight: 400;
+  line-height: 1.4;
+  color: #1D1D1B;
+}
+.custom-blog__card-title a {
+  text-decoration: none;
+  color: #1a1a1a;
+}
+.custom-blog__card-excerpt-arrow {
+  width: 100%;
+  display: flex;
+  justify-content: space-between;
+  align-items: flex-end;
+  gap: 12px;
+}
+.custom-blog__card-excerpt {
+  font-size: 16px;
+  font-family: 'Roboto', sans-serif;
+  font-weight: 400;
+  line-height: 1.6;
+  color: #636363;
+  max-height: none;
+  overflow: visible;
+  margin: 0;
+}
+.custom-blog__card-arrow {
+  position: static;
+  display: inline-flex;
+  justify-content: center;
+  align-items: center;
+  padding: 0;
+  border-radius: 2px;
+}
+.custom-blog__pagination {
+  text-align: center;
+  margin-bottom: 80px;
+}
+.custom-blog__pagination a,
+.custom-blog__pagination span {
+  margin: 0 4px;
+  padding: 8px 12px;
+  font-size: 14px;
+  text-decoration: none;
+  color: #1a1a1a;
+  border-radius: 4px;
+}
+.custom-blog__pagination .current {
+  font-weight: bold;
+}
+
+@media screen and (max-width: 767px) {
+  .custom-blog__banner {
+    height: 600px;
+    background-position: center;
+  }
+  .custom-blog__banner-inner {
+    top: 354px;
+    left: 24px;
+    gap: 22px;
+    max-width: 282px;
+  }
+  .custom-blog__label {
+    font-size: 14px;
+  }
+  .custom-blog__subtitle {
+    font-size: 14px;
+  }
+  .custom-blog__heading {
+    font-size: 24px;
+    max-width: 100%;
+    line-height: 1.3;
+  }
+  .custom-blog__grid {
+    grid-template-columns: repeat(1, 1fr);
+    margin: 40px 24px;
+  }
+}
+{% endstyle %}
