+
+  <!-- Banner -->
+  <div class="custom-blog__banner" style="background-image: url('{{ override_img | default: featured_article.image | image_url: width: 2000 }}'); opacity: {{ section.settings.banner_opacity | divided_by: 100.0 }};">
+    <div class="custom-blog__banner-inner">
+      <div class="custom-blog__label">{{ section.settings.banner_label | default: featured_article.tags.first }}</div>
+      <div class="custom-blog__subtitle">{{ section.settings.banner_subtitle | default: featured_article.author }}</div>
+      <h1 class="custom-blog__heading">{{ section.settings.custom_banner_title | default: featured_article.title }}</h1>
+      <a href="{{ featured_article.url }}" class="custom-blog__arrow">{% render 'diagonal-bottom-left-arrow.svg' %}</a>
+    </div>
+  </div>
+
+  <!-- Blog Slider -->
+  {% assign blog_handle = section.settings.blog | default: 'news' %}
+  {% assign blog_posts = blogs[blog_handle].articles | slice: 0, 10 %}
+  {% if blog_posts.size > 0 %}
+    <section id="blog-slider-{{ section.id }}" class="blog-slider section_width_{{ section.settings.section_width }}" style="background-color: {{ section.settings.background_color }}; padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px; margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;">
+      <div class="slider-carousel">
+        <div class="slider-track">
+          {% for article in blog_posts %}
+            <div class="slider-slide" data-index="{{ forloop.index0 }}">
+              <div class="blog-post-card">
+                {% if article.image %}<img src="{{ article.image | image_url: width: 800 }}" alt="{{ article.title }}">{% endif %}
+                <div class="blog-post-content">
+                  <div class="blog-post-tags">{% for tag in article.tags limit: 2 %}<span class="tag">{{ tag }}</span>{% endfor %}</div>
+                  <div class="blog-post-date">{{ article.published_at | date: "%B %d, %Y" }}</div>
+                  <div class="blog-post-title">{{ article.title }}</div>
+                  <div class="custom-blog__card-excerpt-arrow">
+                    <p class="custom-blog__card-excerpt">{{ article.excerpt_or_content | strip_html | truncate: 140 }}</p>
+                    <a class="custom-blog__card-arrow" href="{{ article.url }}">{% render 'diagonal-bottom-left-arrow.svg' %}</a>
+                  </div>
+                </div>
+              </div>
+            </div>
+          {% endfor %}
