+        {% if section.settings.show_arrows %}<div class="slider-arrows"><button class="slider-arrow prev"></button><button class="slider-arrow next"></button></div>{% endif %}
+      </div>
+    </section>
+  {% endif %}
+
+  <div class="slider-text section_content_width_{{ section.settings.section_content_width }} {{ section.settings.text_alignment }}" style="border-bottom: 1px solid rgba(0, 0, 0, 0.1); padding-bottom: 40px;">
