+    <h2 class="custom-blog__list-title">{{ section.settings.subheading }}</h2>
+    <div class="slider-description">{{ section.settings.description }}</div>
+  </div>
+
+  {% assign selected_tag = request.params.tag %}
+  {% assign sort_option = request.params.sort_by %}
+  {% assign show_all = selected_tag == blank and sort_option == blank %}
+
+  <form method="get" id="blog-filter-form" action="{{ request.path }}">
+    {% unless section.settings.hide_filter_sort %}
+      <div class="custom-blog__filter-sort">
+        <button type="submit" class="custom-blog__filter-btn">Filter</button>
+        <button type="submit" class="custom-blog__sort-btn">Sort</button>
+      </div>
+    {% endunless %}
+
+    <div class="custom-blog__main-layout" style="display: flex; gap: 40px; max-width: 1608px; margin: 0 auto 60px;">
+      <div class="custom-blog__grid" style="flex: 3;">
+        {% paginate blog.articles by 20 %}
+          {% assign filtered = false %}
+          {% for article in paginate.items %}
+            {% assign show_article = true %}
+            {% if selected_tag and selected_tag != blank %}
+              {% unless article.tags contains selected_tag %}
+                {% assign show_article = false %}
+              {% endunless %}
+            {% endif %}
+            {% if sort_option == 'featured' %}
+              {% unless article.tags contains 'featured' %}
+                {% assign show_article = false %}
+              {% endunless %}
+            {% endif %}
+            {% if show_article %}
+              {% assign filtered = true %}
+              <div class="custom-blog__card" data-category="{{ article.tags | join: ' ' }}">
+                <div class="custom-blog__card-image">
+                  {% if article.image %}<a href="{{ article.url }}"><img src="{{ article.image | image_url: width: 600 }}" alt="{{ article.title }}"></a>{% endif %}
+                  {% if article.tags.size > 0 %}<div class="custom-blog__card-label">{{ article.tags.first }}</div>{% endif %}
+                </div>
+                <div class="custom-blog__card-content">
+                  <h3 class="custom-blog__card-title"><a href="{{ article.url }}">{{ article.title }}</a></h3>
+                  <div class="custom-blog__card-excerpt-arrow">
+                    <p class="custom-blog__card-excerpt">{{ article.excerpt_or_content | strip_html | truncate: 140 }}</p>
+                    <a class="custom-blog__card-arrow" href="{{ article.url }}">{% render 'diagonal-bottom-left-arrow.svg' %}</a>
+                  </div>
+                </div>
+              </div>
+            {% endif %}
+          {% endfor %}
+
+          {% if filtered == false %}<p>No articles match your filters.</p>{% endif %}
+
+          <div class="custom-blog__pagination">
+            {% if paginate.previous %}<a href="{{ paginate.previous.url }}">&lt;</a>{% endif %}
+            {% for part in paginate.parts %}
+              {% if part.is_link %}<a href="{{ part.url }}">{{ part.title }}</a>{% else %}<span class="current">{{ part.title }}</span>{% endif %}
+            {% endfor %}
+            {% if paginate.next %}<a href="{{ paginate.next.url }}">&gt;</a>{% endif %}
+          </div>
+        {% endpaginate %}
+      </div>
+
+      <aside class="custom-blog__sidebar">
