+            <li><label><input type="radio" name="sort_by" value="featured" {% if sort_option == 'featured' %}checked{% endif %}> Sylvox Top Picks</label></li>
+          </ul>
+        </details>
+        <details class="custom-blog__dropdown" open>
+          <summary class="custom-blog__dropdown-title">Filter</summary>
+          <ul class="custom-blog__dropdown-list">
+            {% assign tag_options = blog.all_tags %}
+            {% for tag in tag_options %}
+              <li><label><input type="radio" name="tag" value="{{ tag }}" {% if selected_tag == tag %}checked{% endif %}> {{ tag }}</label></li>
+            {% endfor %}
+          </ul>
+        </details>
+      </aside>
+    </div>
+  </form>
+</section>
+
+<script>
+  document.querySelectorAll('#blog-filter-form input[type="radio"]').forEach((radio) => {
+    radio.addEventListener('change', () => {
+      document.getElementById('blog-filter-form').submit();
+    });
+  });
+
+  document.addEventListener('DOMContentLoaded', () => {
+    const section = document.getElementById('blog-slider-{{ section.id }}');
+    if (!section) return;
+
+    const track = section.querySelector('.slider-track');
+    const slides = section.querySelectorAll('.slider-slide');
+    const dots = section.querySelectorAll('.slider-dot');
+    const prevArrow = section.querySelector('.slider-arrow.prev');
+    const nextArrow = section.querySelector('.slider-arrow.next');
+    let current = 0;
+
+    function goTo(index) {
+      if (index < 0 || index >= slides.length) return;
+      current = index;
+      const slide = slides[index];
+      track.scrollTo({ left: slide.offsetLeft - 12, behavior: 'smooth' });
+      slides.forEach((s, i) => s.classList.toggle('active', i === index));
+      dots.forEach((d, i) => d.classList.toggle('active', i === index));
+    }
+
+    dots.forEach((dot, i) => dot.addEventListener('click', () => goTo(i)));
+    prevArrow?.addEventListener('click', () => goTo(current - 1));
+    nextArrow?.addEventListener('click', () => goTo(current + 1));
+    goTo(0);
+  });
+</script>
