+          </div>
+        </div>
+      {% endfor %}
+    </div>
+
+    <aside class="custom-blog__sidebar">
+  <details class="custom-blog__dropdown" open>
+    <summary class="custom-blog__dropdown-title">Sort By</summary>
+    <ul class="custom-blog__dropdown-list">
+      <li><label><input type="radio" name="sort" checked> Latest</label></li>
+      <li><label><input type="radio" name="sort"> Sylvox Top Picks</label></li>
+    </ul>
+  </details>
+
+  <details class="custom-blog__dropdown" open>
+    <summary class="custom-blog__dropdown-title">Filter</summary>
+    <ul class="custom-blog__dropdown-list">
+  {% assign tag_options = blog.all_tags %}
+  {% for tag in tag_options %}
+    <li>
+      <label>
+        <input type="radio" name="filter" value="{{ tag }}" {% if current_tags contains tag %}checked{% endif %}>
+        {{ tag }}
+      </label>
+    </li>
+  {% endfor %}
+</ul>
+
+  </details>
+</aside>
+
+</div>
+
+    <!-- Pagination -->
+    <div class="custom-blog__pagination">
+      {% if paginate.previous %}
+        <a href="{{ paginate.previous.url }}">&lt;</a>
+      {% endif %}
+      {% for part in paginate.parts %}
+        {% if part.is_link %}
+          <a href="{{ part.url }}">{{ part.title }}</a>
+        {% else %}
+          <span class="current">{{ part.title }}</span>
+        {% endif %}
+      {% endfor %}
+      {% if paginate.next %}
+        <a href="{{ paginate.next.url }}">&gt;</a>
+      {% endif %}
+    </div>
+    
+  {% endpaginate %}
+</section>
