+{% style %}
+/* Banner */
+.custom-blog__banner {
+  position: relative;
+  display: flex;
+  align-items: stretch;
+  height: 600px;
+  background-size: cover;
+  background-position: center;
+  color: #ffffff;
+  overflow: hidden;
+}
+
+.custom-blog__banner-overlay {
+  position: absolute;
+  inset: 0;
+  z-index: 1;
+  pointer-events: none;
+}
