commits.content:
{% comment %}
  Custom Section: Feature Showcase | feature-showcase.liquid
{% endcomment %}

<div 
  class="feature-showcase" 
  id="feature-showcase-{{ section.id }}"
  style="
    padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px;
    margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;
  "
>
  {% for block in section.blocks %}
    <div class="feature-showcase__block" {{ block.shopify_attributes }}>
      <div class="feature-showcase__image">
        <img 
          src="{{ block.settings.image | image_url: width: 1600 }}" 
          alt="{{ block.settings.heading | escape }}" 
          loading="lazy"
        />
        <div class="feature-showcase__badge">
          <img src="{{ block.settings.icon | image_url: width: 60 }}" alt="Icon" />
        </div>
      </div>

      <div class="feature-showcase__text">
        <h3 class="feature-showcase__heading">{{ block.settings.heading }}</h3>
        <p class="feature-showcase__subheading">{{ block.settings.subheading }}</p>
        <p class="feature-showcase__description">{{ block.settings.description }}</p>
      </div>
    </div>
  {% endfor %}
</div>

{% style %}
.feature-showcase {
  display: flex;
  flex-direction: column;
  gap: 48px;
}

.feature-showcase__block {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.feature-showcase__image {
  position: relative;
  max-width: 100%;
}

.feature-showcase__image img {
  width: 100%;
  border-radius: 8px;
}

.feature-showcase__badge {
  position: absolute;
  bottom: 16px;
  left: 16px;
  background: rgba(255,255,255,0.8);
  padding: 8px;
  border-radius: 4px;
}

.feature-showcase__text {
  margin-top: 24px;
  max-width: 700px;
}

.feature-showcase__heading {
  font-size: 20px;
  font-weight: 600;
  color: #000;
}

.feature-showcase__subheading {
  font-size: 16px;
  font-weight: 500;
  color: #999;
  margin-top: 4px;
}

