+    {% if section.settings.background_image %}background-image: url({{ section.settings.background_image | img_url: 'master' }}); background-size: cover; background-position: center;{% else %}background-color: {{ section.settings.background_color }};{% endif %}
+  ">
+  <div class="icon-grid__header section_content_width_{{ section.settings.section_content_width }}">
+    {% if section.settings.parent_title %}
+      <h2>{{ section.settings.parent_title }}</h2>
+    {% endif %}
+    {% if section.settings.parent_description %}
+      <p>{{ section.settings.parent_description }}</p>
+    {% endif %}
+    {% if section.settings.parent_button_text != blank and section.settings.parent_button_link != blank %}
+      <a href="{{ section.settings.parent_button_link }}" class="icon-grid__button">{{ section.settings.parent_button_text }}</a>
+    {% endif %}
+    {% if section.settings.parent_image %}
+      <img src="{{ section.settings.parent_image | img_url: 'master' }}" alt="Parent Image" />
+    {% endif %}
+  </div>
+
+  <div class="icon-grid__container {% if section.settings.layout == 'slider' %}icon-grid__slider{% endif %}">
+    {% for block in section.blocks %}
+      <a href="{{ block.settings.link | default: '#' }}" class="icon-grid__item" style="text-align: {{ block.settings.text_align }};">
+        {% if block.settings.icon %}
+          <div class="icon-container icon-align-{{ block.settings.text_align }}">
+            <img src="{{ block.settings.icon | img_url: '150x' }}" style="width: {{ block.settings.icon_size }}%;" alt="{{ block.settings.title }}">
+          </div>
+        {% endif %}
+        <h3 style="color: {{ block.settings.text_color }};">
+          {{ block.settings.title }}
+        </h3>
+        <p class="grid-item-subtitle" style="color: {{ block.settings.text_color }};">
+          {{ block.settings.description }}
+        </p>
+      </a>
+    {% endfor %}
+  </div>
+</section>
+
+<style>
+.icon-grid__container {
+  gap: 2rem;
+}
+
+.icon-grid__container.icon-grid__slider {
+  display: block;
+}
+
+.icon-grid__container:not(.icon-grid__slider) {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
+}
+
+.icon-grid__item {
+  text-decoration: none;
+  padding: 1rem;
+  border-radius: 8px;
+  transition: transform 0.3s ease;
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  text-align: center;
+}
+
+.icon-grid__item:hover {
+  transform: translateY(-5px);
+}
+
+.icon-container {
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  margin-bottom: 1rem;
+  height: 100px; 
+}
+
+.icon-grid__header {
+  text-align: center;
+  margin-bottom: 2rem;
+}
+
+.icon-grid__button {
+  display: inline-block;
+  margin-top: 1rem;
+  padding: 0.5rem 1rem;
+  background: black;
+  color: white;
+  text-decoration: none;
+  border-radius: 5px;
+}
+
+.icon-align-left {
+  justify-content: flex-start;
+}
+
+.icon-align-center {
+  justify-content: center;
+}
+
+.icon-align-right {
+  justify-content: flex-end;
+}
+
+.grid-item-subtitle {
+  font-style: italic;
+}
+</style>
+
+<!-- Flickity -->
+<link rel="stylesheet" href="https://unpkg.com/flickity@2/dist/flickity.min.css">
+<script src="https://unpkg.com/flickity@2/dist/flickity.pkgd.min.js"></script>
+
+<script>
+  document.addEventListener('DOMContentLoaded', function() {
+    {% if section.settings.layout == 'slider' %}
+      var elem = document.querySelector('.icon-grid__slider');
+      if (elem && window.Flickity) {
+        new Flickity( elem, {
+          cellAlign: 'left',
+          contain: true,
+          pageDots: true,
+          prevNextButtons: true
+        });
+      }
+    {% endif %}
+  });
+</script>

