commits.content:
{% schema %}
{
  "name": "Image Blocks",
  "settings": [
     {
  "type": "select",
  "id": "section_width",
  "label": "Section Width",
  "default": "standard",
  "options": [
    {
      "value": "standard",
      "label": "50% Width"
    },
    {
      "value": "full",
      "label": "Full Width"
    }
  ]
},
    {
      "type": "text",
      "id": "section_title",
      "label": "Section Title",
      "default": "Shop by Outdoor Scenarios"
    },
    {
      "type": "textarea",
      "id": "section_description",
      "label": "Section Description",
      "default": "Whether you're enjoying family movie nights, watching the big game with friends, or just unwinding in nature, it brings entertainment where it truly belongs—outdoors."
    }
  ],
  "blocks": [
    {
      "type": "scenario",
      "name": "Scenario Tab",
      "settings": [
        {
          "type": "image_picker",
          "id": "image",
          "label": "Scenario Image"
        },
        {
          "type": "text",
          "id": "title",
          "label": "Scenario Title",
          "default": "By the Pool"
        },
        {
          "type": "text",
          "id": "description",
          "label": "Scenario Description",
          "default": "Enjoy seamless 4K gaming like never before."
        },
        {
          "type": "url",
          "id": "link",
          "label": "Link"
        }
      ]
    }
  ],
  "max_blocks": 6,
  "presets": [
    {
      "name": "Image Blocks",
      "category": "Somos Custom"
    }
  ]
}
{% endschema %}

{% stylesheet %}
.outdoor-scenario-section {
  text-align: center;
  padding: 50px 20px;
}
.outdoor-scenario-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-top: 40px;
}
.outdoor-box {
  position: relative;
  overflow: hidden;
  cursor: pointer;
  border-radius: 10px;
}
.outdoor-box img {
  width: 100%;
  height: auto;
  display: block;
}
.outdoor-box-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background: linear-gradient(to top, rgba(0,0,0,0.6), rgba(0,0,0,0));
  color: white;
  padding: 20px;
  text-align: left;
}
{% endstylesheet %}

<div class="outdoor-scenario-section">
  <h2>{{ section.settings.section_title }}</h2>
  <p>{{ section.settings.section_description }}</p>

  <div class="outdoor-scenario-grid">
    {% for block in section.blocks %}
      {% if block.type == 'scenario' %}
        <a href="{{ block.settings.link }}" class="outdoor-box">
          {% if block.settings.image %}
            <img src="{{ block.settings.image | image_url: width: 600 }}" alt="{{ block.settings.title }}">
          {% endif %}
          <div class="outdoor-box-overlay">
            <h3>{{ block.settings.title }}</h3>
            <p>{{ block.settings.description }}</p>
          </div>
        </a>
      {% endif %}
    {% endfor %}
  </div>
</div>
