{% schema %}
{
  "name": "Join Community",
  "tag": "section",
  "class": "join-community-section",
  "settings": [
    {
      "type": "image_picker",
      "id": "logo_image",
      "label": "Logo Image"
    },
    {
      "type": "range",
      "id": "logo_size",
      "label": "Logo Size (%)",
      "min": 10,
      "max": 100,
      "step": 5,
      "default": 100
    },
    {
      "type": "text",
      "id": "subheading",
      "label": "Subheading",
      "default": "Facebook Community"
    },
    {
      "type": "text",
      "id": "heading",
      "label": "Heading",
      "default": "Join Sylvox Community"
    },
    {
      "type": "url",
      "id": "heading_link",
      "label": "Heading Link"
    },
    {
      "type": "image_picker",
      "id": "community_image",
      "label": "Community Image"
    }
  ],
  "presets": [
    {
      "name": "Join Community",
      "category": "Somos Custom"
    }
  ]
}
{% endschema %}


<div class="join-community-wrapper">
  <div class="join-community-left" style="max-width: {{ section.settings.logo_size }}%;">
    {% if section.settings.logo_image %}
      <img src="{{ section.settings.logo_image | image_url }}" alt="Logo">
    {% endif %}
  </div>
  <div class="join-community-center">
    {% if section.settings.subheading %}
      <small>{{ section.settings.subheading }}</small>
    {% endif %}
    {% if section.settings.heading_link %}
      <a href="{{ section.settings.heading_link }}">{{ section.settings.heading }}</a>
    {% else %}
      <span class="heading">{{ section.settings.heading }}</span>
    {% endif %}
  </div>
  <div class="join-community-right">
    {% if section.settings.community_image %}
      <img src="{{ section.settings.community_image | image_url }}" alt="Community Image">
    {% endif %}
  </div>
</div>
