+                      {% if product.compare_at_price > product.price %}
+                        <span class="keyword-filter-grid__compare">{{ product.compare_at_price | money }}</span>
+                      {% endif %}
+                    </div>
+                  </div>
+                </a>
+              </div>
+            {% endfor %}
+          </div>
+          {% assign first = false %}
+        {% endif %}
+      {% endfor %}
+      <div class="keyword-filter-grid__mobile">
