+}
+.keyword-filter-grid__header {
+  background-size: cover;
+  background-position: center;
+  background-repeat: no-repeat;
+  height: 200px;
+}
+.keyword-filter-grid__header-content {
+  display: flex;
+  justify-content: center;
+  align-items: center;
+  flex-wrap: wrap;
+  gap: 40px;
+  height: 100%;
+}
+.keyword-filter-grid__header-left {
+  max-width: 510px;
+  flex: 1 1 510px;
+  display: flex;
+  flex-direction: column;
+  gap: 15px;
+}
+.keyword-filter-grid__title {
+  font-size: 56px;
+  font-family: "Questrial", sans-serif;
+  font-weight: 600;
+  margin: 0;
+}
+.keyword-filter-grid__description {
+  font-size: 16px;
+  font-family: "Roboto", sans-serif;
+}
+.keyword-filter-grid__search-bar-wrapper {
+  flex: 1 1 420px;
+  max-width: 420px;
+  display: flex;
+  justify-content: center;
+}
+.keyword-filter-grid__search-bar-wrapper form {
+  width: 100%;
+}
+.keyword-filter-grid__search-bar {
+  width: 100%;
+  height: 82px;
+  display: flex;
+  align-items: center;
+  border-radius: 100px;
+  background: #515a5c;
+  color: white;
+  overflow: hidden;
+}
+.keyword-filter-grid__search-bar input {
+  background: transparent;
+  border: none;
+  color: white;
+  padding: 20px;
+  font-size: 16px;
+  flex-grow: 1;
+}
+.keyword-filter-grid__search-bar input::placeholder {
+  color: white;
+  opacity: 1;
+}
+.keyword-filter-grid__search-button {
+  width: 82px;
+  height: 82px;
+  padding: 0;
+  background: transparent;
+  border: none;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  cursor: pointer;
+}
+.keyword-filter-grid__search-button svg {
+  width: 48px;
+  height: 48px;
   display: block;
+}
+.keyword-filter-grid__filters-stack {
+  flex: 1 1 auto;
+  max-width: 420px;
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  gap: 15px;
+}
+.keyword-filter-grid__filter-label {
+  font-size: 14px;
+  font-family: "Roboto", sans-serif;
+  color: #535353;
+  text-align: center;
+}
+.keyword-filter-grid__checkboxes {
+  display: flex;
+  flex-wrap: wrap;
+  gap: 16px;
+  font-size: 13px;
+  font-family: "Roboto", sans-serif;
+  justify-content: center;
+}
+.keyword-filter-grid__checkboxes label {
+  display: flex;
+  align-items: center;
+  gap: 6px;
+  color: #535353;
+}
+.keyword-filter-grid__tags {
+  display: flex;
+  flex-wrap: wrap;
+  gap: 12px;
+  justify-content: center;
+}
+.keyword-filter-grid__tags button {
+  font-size: 12px;
+  padding: 6px 14px;
+  border: 1px solid var(--text);
+  border-radius: 30px;
+  background: none;
+  cursor: pointer;
+  color: #535353;
+  font-family: "Roboto", sans-serif;
+}
+.keyword-filter-grid__tabs-row {
+  display: flex;
+  align-items: center;
+  gap: 40px;
+  margin: 40px 0 30px;
+}
+.keyword-filter-grid__tabs {
+  display: flex;
+  gap: 32px;
+  font-size: 24px;
+}
+.keyword-filter-grid__tab {
+  cursor: pointer;
+  opacity: 0.5;
+}
+.keyword-filter-grid__tab.active {
+  opacity: 1;
+  font-weight: 500;
+  text-decoration: underline;
+}
+.keyword-filter-grid__shop-all {
+  margin-left: auto;
+  padding: 8px 20px;
+  font-size: 14px;
+  border: 1px solid var(--text);
+  border-radius: 50px;
+  text-decoration: none;
+  color: var(--text);
+}
+.keyword-filter-grid__grid {
+  display: none;
+  grid-template-columns: repeat(5, 1fr);
+  gap: 40px;
+  max-width: 1604px;
+  margin: 0 auto;
+}
+.keyword-filter-grid__grid.active {
+  display: grid;
+}
+.keyword-filter-grid__product-card {
+  display: flex;
+  flex-direction: column;
+  justify-content: space-between;
+  max-width: 283px;
+  max-height: 322px;
+  aspect-ratio: 283 / 322;
+  text-align: left;
+}
+.keyword-filter-grid__thumbnail {
   width: 100%;
   height: auto;
+  aspect-ratio: 283 / 322;
+  overflow: hidden;
 }
-.keyword-filter-grid__side-image--left {
-  float: left;
-  margin-right: 20px;
+.keyword-filter-grid__thumbnail img {
+  width: 100%;
+  height: 100%;
+  object-fit: contain;
 }
-.keyword-filter-grid__side-image--right {
-  float: right;
-  margin-left: 20px;
+.keyword-filter-grid__info {
+  margin-top: 10px;
+  flex-shrink: 0;
 }
+.keyword-filter-grid__label {
+  font-size: 14px;
+  color: #2dac4e;
+  font-family: "Roboto", sans-serif;
+}
+.keyword-filter-grid__title-text {
+  font-size: 18px;
+  font-family: "Questrial", sans-serif;
+}
+.keyword-filter-grid__price {
+  font-size: 14px;
+  font-family: "Roboto", sans-serif;
+}
+.keyword-filter-grid__compare {
+  text-decoration: line-through;
+  opacity: 0.5;
+  margin-left: 8px;
+}
+@media screen and (max-width: 1000px) {
+  .keyword-filter-grid__inner {
+    padding: 0 20px;
+  }
+}
