{% schema %}
{
  "name": "Large product",
  "settings": [
    {
      "type": "text",
      "id": "subtitle",
      "label": "Subtitle",
      "default": "Outdoor Entertainment, Perfected"
    },
    {
      "type": "textarea",
      "id": "title",
      "label": "Title",
      "default": "Sylvox Deck Pro 2.0"
    },
    {
      "type": "image_picker",
      "id": "icon1",
      "label": "Icon 1"
    },
    {
      "type": "image_picker",
      "id": "icon2",
      "label": "Icon 2"
    },
    {
      "type": "image_picker",
      "id": "icon3",
      "label": "Icon 3"
    },
    {
      "type": "product",
      "id": "product",
      "label": "Product"
    },
    {
  "type": "checkbox",
  "id": "show_title",
  "label": "Show product title",
  "default": true
},
    {
      "type": "text",
      "id": "price_text",
      "label": "Price Text",
      "default": "From USD $1,149.00"
    },
    {
      "type": "url",
      "id": "button_link",
      "label": "Button Link"
    },
    {
      "type": "text",
      "id": "button_label",
      "label": "Button Label",
      "default": "Buy Now"
    }
  ],
  "presets": [
    {
      "name": "Large product",
      "category": "Somos Custom"
    }
  ]
}
{% endschema %}

{% stylesheet %}
.sylvox-section {
  text-align: center;
  padding: 40px 20px;
}

.sylvox-icons {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin: 20px 0;
}

.sylvox-icons img {
  height: 30px;
}

.sylvox-products {
  margin-top: 20px;
}
{% endstylesheet %}

<div class="sylvox-section">
  <p>{{ section.settings.subtitle }}</p>
  <h2>{{ section.settings.title | newline_to_br }}</h2>

  <div class="sylvox-icons">
    {% if section.settings.icon1 %}
      <img src="{{ section.settings.icon1 | image_url }}" alt="Icon 1" />
    {% endif %}
    {% if section.settings.icon2 %}
      <img src="{{ section.settings.icon2 | image_url }}" alt="Icon 2" />
    {% endif %}
    {% if section.settings.icon3 %}
      <img src="{{ section.settings.icon3 | image_url }}" alt="Icon 3" />
    {% endif %}
  </div>

  <div class="sylvox-products">
    {% if section.settings.product %}
  <div class="product-display">
    {% if section.settings.product.featured_image %}
      <img src="{{ section.settings.product.featured_image | image_url }}" alt="{{ section.settings.product.title }}">
    {% endif %}
    {% if section.settings.show_title %}
      <p><strong>{{ section.settings.product.title }}</strong></p>
    {% endif %}
  </div>
{% endif %}
  </div>

  <p>{{ section.settings.price_text }}</p>
  <a href="{{ section.settings.button_link }}" class="button">
    {{ section.settings.button_label }}
  </a>
</div>
