+  .carousel-wrapper {
+    position: relative;
+    max-width: 100%;
+  }
+
+  .carousel-arrows-wrapper {
+    display: flex;
+    justify-content: flex-end;
+  }
+
+  .flickity-carousel {
+    overflow: visible;
+  }
+
+  .carousel-cell {
+    margin-right: 20px;
+    opacity: 0.3;
+    transform: scale(0.94);
+    transition: all 0.3s ease;
+  }
+
+  .carousel-cell.is-selected {
+    opacity: 1;
+    transform: scale(1);
+  }
+
+  .carousel-cell img {
+    width: 100%;
+    border-radius: 8px;
+  }
+
+  .carousel-cell.size-small { width: 40%; }
+  .carousel-cell.size-medium { width: 55%; }
+  .carousel-cell.size-large { width: 70%; }
+
+  .carousel-caption {
+    position: absolute;
+    bottom: 2rem;
+    left: 1rem;
+    font-size: 0.75rem;
+    color: white;
+    font-weight: 400;
+  }
+
+   .carousel-description_block {
+    position: absolute;
+    bottom: 1rem;
+    left: 1rem;
+    font-size: 0.75rem;
+    color: white;
+    font-weight: 400;
+  }
+
+  .carousel-arrows {
+    display: flex;
+    gap: 10px;
+    position: absolute;
+    bottom: -55px;
+    right: 0;
+  }
+
+  .carousel-arrows button {
+    width: 40px;
+    height: 40px;
+    border-radius: 50%;
+    border: 1px solid #000;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    background: white;
+    cursor: pointer;
+    transition: border-color 0.3s;
+  }
+
+  .carousel-arrows button:hover {
+    border-color: #999;
+  }
+
+  .carousel-arrows button svg {
+    width: 16px;
+    height: 16px;
+    fill: #333;
+  }
+</style>
+{% endstyle %}
+
+
+<section class="outdoor-carousel-section section_width_{{ section.settings.section_width }}" style="padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px; margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px">
+  <div class="outdoor-carousel-text section_content_width_{{ section.settings.section_content_width }} {{ section.settings.text_alignment }}">
+    <div class="small-heading">{{ section.settings.heading }}</div>
+    <h2>{{ section.settings.subheading }}</h2>
+  </div>
+
+  <div class="carousel-wrapper section_width_{{ section.settings.section_width }}">
+    <div class="flickity-carousel"
+         data-flickity='{
+           "wrapAround": true,
+           "imagesLoaded": true,
+           "pageDots": true,
+           "prevNextButtons": false,
+           "cellAlign": "center",
+           "contain": true
+         }'>
+      {% for block in section.blocks %}
+        <div class="carousel-cell size-{{ section.settings.slide_size }} relative">
+          {% if block.settings.image %}
+            <img src="{{ block.settings.image | img_url: 'master' }}" alt="Slide Image">
+          {% endif %}
+          {% if block.settings.caption %}
+            <div class="carousel-caption">{{ block.settings.caption }}</div>
+          {% endif %}
+          {% if block.settings.description_block %}
+            <div class="carousel-description_block">{{ block.settings.description_block }}</div>
+          {% endif %}
+        </div>
+      {% endfor %}
+    </div>
+  </div>
+
+  <!-- Dots and Arrows Container -->
+  <div class="controls dots-arrows-wrapper section_content_width_{{ section.settings.section_content_width }}" style="display: flex; justify-content: space-between; align-items: center; margin-top: 16px;">
+    <div class="carousel-dots-container">
+      <div class="carousel-dots"></div>
+    </div>
+
+    {% if section.settings.show_arrows %}
+      <div class="carousel-arrows content-right">
+        <button class="usp-arrow prev" aria-label="Previous">‹</button>
+        <button class="usp-arrow next" aria-label="Next">›</button>
+      </div>
+      <div class="carousel-arrows">
+        <button class="carousel-prev" aria-label="Previous">
+          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M15 18l-6-6 6-6"/></svg>
+        </button>
+        <button class="carousel-next" aria-label="Next">
+          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24"><path d="M9 6l6 6-6 6"/></svg>
+        </button>
+      </div>
+    {% endif %}
+  </div>
+</section>
+
+{% javascript %}
+<script>
+  document.addEventListener("DOMContentLoaded", function () {
+    const carousel = document.querySelector('.flickity-carousel');
+    const customDotsContainer = document.querySelector('.carousel-dots');
+
+    // Fully disable Flickity's default dot handling
+    let flkty = new Flickity(carousel, {
+      wrapAround: true,
+      imagesLoaded: true,
+      pageDots: false, // prevent auto-adding to DOM
+      prevNextButtons: false,
+      cellAlign: "center",
+      contain: true
+    });
+
+    // Manually generate page dots and move them inside .carousel-dots
+    flkty.on('ready', function () {
+      if (typeof flkty.createPageDots === 'function') {
+        flkty.createPageDots(); // create <ol class="flickity-page-dots">
+      }
+      if (flkty.pageDots && flkty.pageDots.element && customDotsContainer) {
+        customDotsContainer.appendChild(flkty.pageDots.element);
+      }
+    });
+
+    // Navigation arrows
+    document.querySelector('.carousel-prev')?.addEventListener('click', () => flkty.previous());
+    document.querySelector('.carousel-next')?.addEventListener('click', () => flkty.next());
+  });
+</script>
+{% endjavascript %}
+
+

