{% comment %}
  Custom section: Product Comparison | product-comparison.liquid
{% endcomment %}
<section class="product-comparison-section">
  <div class="comparison-container">
    <div class="comparison-header">
      <h2>{{ section.settings.heading }}</h2>
      <p>{{ section.settings.subheading }}</p>
    </div>

    <div class="comparison-products">
      {% for product in section.settings.products %}
        <div class="comparison-product">
          {% if product.featured_image %}
            <img
              src="{{ product.featured_image | image_url: width: 300 }}"
              alt="{{ product.title }}"
              class="product-image"
            >
          {% else %}
            <div class="image-placeholder"></div>
          {% endif %}
          <div class="product-title">{{ product.title }}</div>
          <div class="product-price">{{ product.price | money }}</div>
          <a href="{{ product.url }}" class="view-button">
            View
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="17" viewBox="0 0 16 17" fill="none">
              <path d="M12 12.3047L4.61985 12.2853V10.91L9.7724 11.0456L4 5.29258L4.98789 4.30469L10.7603 10.0577L10.6441 4.90517H12V12.3047Z" fill="white"/>
            </svg>
          </a>
        </div>
      {% endfor %}
    </div>

    <div class="comparison-table">
      <div class="comparison-row">
        <div class="feature-name"><strong>{{ section.settings.feature_1_name }}</strong></div>
        <div class="feature-value">{{ section.settings.feature_1_key }}</div>
        <div class="feature-value">{{ section.settings.feature_1_key }}</div>
        <div class="feature-value">{{ section.settings.feature_1_key }}</div>
      </div>
      <div class="comparison-row">
        <div class="feature-name"><strong>{{ section.settings.feature_2_name }}</strong></div>
        <div class="feature-value">{{ section.settings.feature_2_key }}</div>
        <div class="feature-value">{{ section.settings.feature_2_key }}</div>
        <div class="feature-value">{{ section.settings.feature_2_key }}</div>
      </div>
      <div class="comparison-row">
        <div class="feature-name"><strong>{{ section.settings.feature_3_name }}</strong></div>
        <div class="feature-value">{{ section.settings.feature_3_key }}</div>
        <div class="feature-value">{{ section.settings.feature_3_key }}</div>
        <div class="feature-value">{{ section.settings.feature_3_key }}</div>
      </div>
      <div class="comparison-row">
        <div class="feature-name"><strong>{{ section.settings.feature_4_name }}</strong></div>
        <div class="feature-value">{{ section.settings.feature_4_key }}</div>
        <div class="feature-value">{{ section.settings.feature_4_key }}</div>
        <div class="feature-value">{{ section.settings.feature_4_key }}</div>
      </div>
      <div class="comparison-row last-row">
        <div class="feature-name"><strong>{{ section.settings.feature_5_name }}</strong></div>
        <div class="feature-value">{{ section.settings.feature_5_key }}</div>
        <div class="feature-value">{{ section.settings.feature_5_key }}</div>
        <div class="feature-value">{{ section.settings.feature_5_key }}</div>
      </div>
    </div>

    <div class="view-all-wrapper">
      <button class="view-all-button">
        View all
        <svg width="24" height="24" viewBox="0 0 49 48" fill="none" xmlns="http://www.w3.org/2000/svg" style="transform: rotate(90deg);">
          <g clip-path="url(#clip0_552_3992)">
            <path opacity="0.196216" d="M24.5 48C37.7548 48 48.5 37.2548 48.5 24C48.5 10.7452 37.7548 0 24.5 0C11.2452 0 0.5 10.7452 0.5 24C0.5 37.2548 11.2452 48 24.5 48Z" fill="#1A1A1A"/>
            <path d="M21.7414 29.7473L27.6441 24.5002L21.7414 19.2532C21.5445 19.0781 21.4594 18.8152 21.5184 18.5635C21.5773 18.3118 21.7712 18.1094 22.0271 18.0327C22.283 17.956 22.5619 18.0165 22.7588 18.1915L29.2586 23.9692C29.4125 24.1059 29.5 24.2984 29.5 24.5C29.5 24.7016 29.4125 24.8941 29.2586 25.0308L22.7588 30.8085C22.4544 31.079 21.9799 31.0607 21.6989 30.7676C21.4179 30.4744 21.437 30.0179 21.7414 29.7473Z" fill="#1A1A1A"/>
          </g>
          <defs>
            <clipPath id="clip0_552_3992">
              <rect width="48" height="48" fill="white" transform="translate(0.5)"/>
            </clipPath>
          </defs>
        </svg>
      </button>
    </div>
  </div>
</section>

{% style %}
.product-comparison-section {
  padding: 40px 20px;
}
.comparison-container {
  max-width: 1608px;
  margin: 0 auto;
}
.comparison-header h2 {
  font-family: 'Questrial', sans-serif;
  font-size: 48px;
  margin-bottom: 6px;
  color: #1a1a1a;
  text-align: left;
}
.comparison-header p {
  font-family: 'Roboto', sans-serif;
  font-size: 18px;
  color: #1a1a1a;
  margin-bottom: 36px;
  text-align: left;
}
.comparison-products {
  display: flex;
  justify-content: center;
  gap: 48px;
  margin-bottom: 40px;
}
.comparison-product {
  flex: 0 0 300px;
  text-align: left;
}
.product-image {
  max-height: 180px;
  margin-bottom: 12px;
  margin-left: auto;
  margin-right: auto;
  display: block;
}
.image-placeholder {
  height: 180px;
  background: #f4f4f4;
  margin-bottom: 12px;
}
.product-title {
  font-family: 'Questrial', sans-serif;
  font-size: 18px;
  color: #1a1a1a;
  margin-bottom: 6px;
}
.product-price {
  font-family: 'Roboto', sans-serif;
  font-size: 16px;
  color: rgba(26, 26, 26, 0.7);
  margin-bottom: 12px;
}
.view-button {
  background: linear-gradient(90deg, #57a653, #1ba613);
  color: #fff;
  padding: 12px 20px;
  font-size: 14px;
  font-family: 'Roboto', sans-serif;
  border-radius: 30px;
  text-decoration: none;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  border: none;
  cursor: pointer;
}
.view-button svg {
  flex-shrink: 0;
}
.comparison-table {
  border-top: 1px solid rgba(26, 26, 26, 0.12);
}
.comparison-row {
  display: grid;
  grid-template-columns: 300px repeat(3, 300px);
  align-items: center;
  height: 74.6px;
  padding: 0 0 0 0;
  border-top: 1px solid rgba(26, 26, 26, 0.12);
  gap: 48px;
}
.comparison-row.last-row {
  border-bottom: none;
}
.feature-name {
  font-family: 'Barlow', 'Roboto', sans-serif;
  font-weight: 700;
  font-size: 16px;
  color: #1a1a1a;
  padding-left: 16px;
}
.feature-value {
  font-family: 'Roboto', sans-serif;
  font-size: 16px;
  color: rgba(26, 26, 26, 0.7);
  text-align: left;
  display: flex;
  justify-content: flex-start;
  align-items: center;
}
.comparison-row.last-row .feature-value svg {
  margin: 0 auto;
}
.view-all-wrapper {
  text-align: center;
  margin-top: 24px;
}
.view-all-button {
  font-family: 'Basic Sans', sans-serif;
  font-size: 13px;
  color: #000;
  background: none;
  border: none;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 0;
}
.view-all-button svg {
  flex-shrink: 0;
}
@media screen and (max-width: 768px) {
  .comparison-products {
    flex-direction: column;
    gap: 32px;
  }
  .comparison-row {
    grid-template-columns: 1fr;
    text-align: left;
    height: auto;
    border-top: 1px solid #d9d9d9;
  }
  .feature-name {
    padding-left: 0;
  }
  .feature-value {
    justify-content: flex-start;
    padding-left: 16px;
  }
}
{% endstyle %}

{% schema %}
{
  "name": "Product Comparison",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "Heading",
      "default": "Compare"
    },
    {
      "type": "text",
      "id": "subheading",
      "label": "Subheading",
      "default": "Compare our most popular products below."
    },
    {
      "type": "product_list",
      "id": "products",
      "label": "Select Products",
      "limit": 3
    },
    {
      "type": "text",
      "id": "feature_1_name",
      "label": "Feature 1 name",
      "default": "Level"
    },
    {
      "type": "text",
      "id": "feature_2_name",
      "label": "Feature 2 name",
      "default": "Voice Assistant"
    },
    {
      "type": "text",
      "id": "feature_3_name",
      "label": "Feature 3 name",
      "default": "Channel Dashcam"
    },
    {
      "type": "text",
      "id": "feature_4_name",
      "label": "Feature 4 name",
      "default": "Ear Pads"
    },
    {
      "type": "text",
      "id": "feature_5_name",
      "label": "Feature 5 name",
      "default": "Voice Assistant"
    },
    {
      "type": "text",
      "id": "feature_1_key",
      "label": "Feature 1 value (use dynamic source)",
      "default": "xxxx",
      "info": "Click the dynamic source icon to connect a product metafield"
    },
    {
      "type": "text",
      "id": "feature_2_key",
      "label": "Feature 2 value (use dynamic source)",
      "default": "xxxx",
      "info": "Click the dynamic source icon to connect a product metafield"
    },
    {
      "type": "text",
      "id": "feature_3_key",
      "label": "Feature 3 value (use dynamic source)",
      "default": "xxxx",
      "info": "Click the dynamic source icon to connect a product metafield"
    },
    {
      "type": "text",
      "id": "feature_4_key",
      "label": "Feature 4 value (use dynamic source)",
      "default": "xxxx",
      "info": "Click the dynamic source icon to connect a product metafield"
    },
    {
      "type": "text",
      "id": "feature_5_key",
      "label": "Feature 5 value (use dynamic source)",
      "default": "xxxx",
      "info": "Click the dynamic source icon to connect a product metafield"
    }
  ],
  "presets": [
    {
      "name": "Product Comparison",
      "category": "Custom"
    }
  ]
}
{% endschema %}
