@@ -1,4 +1,82 @@
+<style>
+  .section_width_standard,
+  .section_width_full {
+    transition: all 0.3s ease;
+  }
+  .section_width_full {
+    width: 100%;
+  }
+  .section_width_standard {
+    margin: 0 auto !important;
+    width: 90% !important;
+  }
+
+  .section_content_width_standard,
+  .section_content_width_full {
+    transition: all 0.3s ease;
+  }
+  .section_content_width_full {
+    width: 100%;
+  }
+  .section_content_width_standard {
+    margin: 0 auto !important;
+    width: 90% !important;
+  }
+
+  #shopify-section-{{ section.id }} {
+    padding-top: {{ section.settings.padding_top }}px;
+    padding-bottom: {{ section.settings.padding_bottom }}px;
+    padding-left: {{ section.settings.padding_left }}px;
+    padding-right: {{ section.settings.padding_right }}px;
+    background-color: {{ section.settings.background_color }};
+    {% if section.settings.background_image != blank %}
+      background-image: url({{ section.settings.background_image | img_url: 'master' }});
+      background-size: cover;
+      background-position: center;
+    {% endif %}
+  }
+</style>
+
+<div class="section_width_{{ section.settings.section_width }}">
+  <div class="section_content_width_{{ section.settings.section_content_width }}">
+    <div class="grid grid-cols-1">
+      <div class="prose {{ section.settings.mobile_text_position }} {{ section.settings.desktop_text_position }}">
+        {% for block in section.blocks %}
+          {% case block.type %}
+            {% when 'subheading' %}
+              {% if block.settings.text != blank %}
+                <p class="bold" style="color: {{ block.settings.color }}; font-size: {{ block.settings.font_size }}px;" {{ block.shopify_attributes }}>
+                  {{ block.settings.text | escape }}
+                </p>
+              {% endif %}
+
+            {% when 'heading' %}
+              {% if block.settings.text != blank %}
+                <p class="{{ block.settings.heading_tag }}" style="color: {{ block.settings.color }}; font-size: {{ block.settings.font_size }}px;" {{ block.shopify_attributes }}>
+                  {{ block.settings.text | escape }}
+                </p>
+              {% endif %}
+
+            {% when 'richtext' %}
+              {% if block.settings.content != blank %}
+                <div style="color: {{ block.settings.color }}; font-size: {{ block.settings.font_size }}px;" {{ block.shopify_attributes }}>
+                  {{ block.settings.content }}
+                </div>
+              {% endif %}
+
+            {% when 'image' %}
+              {% if block.settings.image != blank %}
+                <img src="{{ block.settings.image | image_url: '800x' }}" style="width: {{ block.settings.image_width }}%;" alt="{{ block.settings.image.alt | escape }}" {{ block.shopify_attributes }}>
+              {% endif %}
+
+          {% endcase %}
+        {% endfor %}
+      </div>
+    </div>
+  </div>
+</div>
+
