--- 
+++ 
@@ -0,0 +1,371 @@
+
+
+<style>
+  .usp-container {
+    display: flex;
+    flex-wrap: wrap;
+    justify-content: center;
+    gap: 8px;
+    align-content: start;
+    margin: 0 auto;
+  }
+  .usp-item {
+    flex: 1 1 calc(33.333% - 16px);
+    box-sizing: border-box;
+    text-align: center;
+    padding: 16px;
+    border-radius: 8px;
+    position: relative;
+  }
+  .usp-item img {
+    max-width: 100%;
+    height: auto;
+    display: block;
+    margin: 0 auto;
+    width: 100%;
+  }
+  .content-block {
+    box-sizing: border-box;
+    text-align: center;
+    position: relative;
+  }
+  .content-block img {
+    max-width: 100%;
+    height: auto;
+    display: block;
+    margin: 0 auto;
+    width: 100%;
+  }
+  .hover-zoom img:hover { transform: scale(1.05); }
+  .hover-grayscale img:hover { filter: grayscale(100%); }
+  .hover-fade img:hover { opacity: 0.7; }
+</style>
+
+<div id="{{ section.settings.section_id }}"
+  class="usp-section section_width_{{ section.settings.section_width }} section_content_width_{{ section.settings.section_content_width }} {{ section.settings.custom_class }}
+    {% unless section.settings.show_on_desktop %}hide-desktop{% endunless %}
+    {% unless section.settings.show_on_tablet %}hide-tablet{% endunless %}
+    {% unless section.settings.show_on_mobile %}hide-mobile{% endunless %}"
+  style="
+    background: {{ section.settings.background_image | image_url: 'original' }} no-repeat center/cover, {{ section.settings.background_color }};
+    padding-top: {{ section.settings.padding_top }}px;
+    padding-right: {{ section.settings.padding_right }}px;
+    padding-bottom: {{ section.settings.padding_bottom }}px;
+    padding-left: {{ section.settings.padding_left }}px;
+  "
+>
+  {% if section.settings.sub_text != blank %}
+    <div class="usp-subtext"
+         style="text-align: {{ section.settings.header_alignment }}; color: {{ section.settings.sub_color }}; font-weight: {{ section.settings.sub_weight }}; margin-bottom: {{ section.settings.top_title_padding_bottom }}px;">
+      {{ section.settings.sub_text }}
+    </div>
+  {% endif %}
+
+  {% if section.settings.header_text != blank %}
+    <{{ section.settings.header_tag }}
+      class="usp-header"
+      style="text-align: {{ section.settings.header_alignment }}; color: {{ section.settings.header_color }}; font-weight: {{ section.settings.header_weight }};">
+      {{ section.settings.header_text }}
+    </{{ section.settings.header_tag }}>
+  {% endif %}
+
+  <div class="usp-container {% if section.settings.slider_mode == 'slider' %}slider-wrapper{% endif %}">
+    {% for block in section.blocks %}
+      {% if block.type == 'usp' %}
+        <div class="usp-item {{ block.settings.hover_effect }}">
+          {% if block.settings.image != blank %}
+            <img
+              src="{{ block.settings.image | image_url: width: 800 }}"
+              loading="{% if block.settings.lazy_load %}lazy{% else %}eager{% endif %}"
+              style="width: {{ block.settings.image_width }}%; aspect-ratio: {{ block.settings.image_ratio }};"
+            >
+          {% endif %}
+          {% if block.settings.title != blank %}
+            <{{ block.settings.title_tag }} style="color: {{ block.settings.title_color }}; font-weight: {{ block.settings.title_weight }};">
+              {{ block.settings.title }}
+            </{{ block.settings.title_tag }}>
+          {% endif %}
+          {% if block.settings.text != blank %}
+            <div class="usp-description">
+              {{ block.settings.text }}
+            </div>
+          {% endif %}
+        </div>
+
+      {% elsif block.type == 'content_block' %}
+        <div
+          class="content-block {{ block.settings.custom_class }}"
+          id="{{ block.settings.custom_id }}"
+          style="
+            text-align: {{ block.settings.horizontal_alignment }};
+            display: flex;
+            flex-direction: column;
+            justify-content: {% if block.settings.vertical_alignment == 'top' %}flex-start{% elsif block.settings.vertical_alignment == 'bottom' %}flex-end{% else %}center{% endif %};
+            max-width: {{ block.settings.content_width | default: 100 }}%;
+            width: {{ block.settings.content_width | default: 100 }}%;
+            margin-left: auto;
+            margin-right: auto;
+            padding-top: {{ block.settings.padding_top | default: 0 }}px;
+            padding-right: {{ block.settings.padding_right | default: 0 }}px;
+            padding-bottom: {{ block.settings.padding_bottom | default: 0 }}px;
+            padding-left: {{ block.settings.padding_left | default: 0 }}px;
+            background:
+              {% if block.settings.background_image != blank %}url('{{ block.settings.background_image | image_url: width: 1500 }}') no-repeat center/cover,{% endif %}
+              {{ block.settings.background_color | default: 'transparent' }};
+          "
+        >
+          {% if block.settings.image != blank %}
+            <img
+              src="{{ block.settings.image | image_url }}"
+              style="width: {{ block.settings.image_width }}%; margin-bottom: 16px; color: {{ block.settings.title_color }}; padding-bottom: {{ block.settings.top_padding_bottom | default: 0 }}px">
+          {% endif %}
+
+          {% if block.settings.sub_title != blank %}
+            <div class="content-subtitle" style="color: {{ block.settings.title_color }};">{{ block.settings.sub_title }}</div>
+          {% endif %}
+
+          {% if block.settings.title != blank %}
+            <{{ block.settings.title_tag }}
+              style="color: {{ block.settings.title_color }}; font-weight: {{ block.settings.title_weight }}; {% if block.settings.title_italic %}font-style: italic;{% endif %}">
+              {{ block.settings.title }}
+            </{{ block.settings.title_tag }}>
+          {% endif %}
+
+          {% unless block.settings.text_at_bottom %}
+            {% if block.settings.text != blank %}
+              <div class="content-text" style="color: {{ block.settings.title_color }};">
+                {{ block.settings.text | raw }}
+              </div>
+            {% endif %}
+          {% endunless %}
+
+          {% if block.settings.button_text != blank and block.settings.button_link != blank %}
+            <a href="{{ block.settings.button_link }}" class="button {{ block.settings.button_style }}">
+              {% if block.settings.button_icon_position == 'left' and block.settings.button_icon != blank %}
+                <img src="{{ block.settings.button_icon | image_url: width: 20 }}" alt="" style="margin-right: 8px; display: inline-block;">
+              {% endif %}
+              {{ block.settings.button_text }}
+              {% if block.settings.button_icon_position == 'right' and block.settings.button_icon != blank %}
+                <img src="{{ block.settings.button_icon | image_url: width: 20 }}" alt="" style="margin-left: 8px; display: inline-block;">
+              {% endif %}
+            </a>
+          {% endif %}
+
+          {% if block.settings.text_at_bottom and block.settings.text != blank %}
+            <div class="content-text" style="color: {{ block.settings.title_color }};">
+              {{ block.settings.text | raw }}
+            </div>
+          {% endif %}
+        </div>
+      {% endif %}
+    {% endfor %}
+  </div>
+</div>
+
+{% schema %}
+{
+  "name": "Somos Grid",
+  "tag": "section",
+  "class": "usp-section-wrapper",
+  "settings": [
+    { "type": "text", "id": "header_text", "label": "Title", "default": "Why Choose Us" },
+    { "type": "text", "id": "sub_text", "label": "Sub Text" },
