{% schema %}
{
  "name": "Somos Content/Image",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "Parent Title",
      "default": "Discover What Sets Us Apart"
    },
    {
      "type": "textarea",
      "id": "description",
      "label": "Parent Description",
      "default": "Experience unmatched performance and innovation with every feature."
    },
    {
      "type": "text",
      "id": "button_label",
      "label": "Button Label",
      "default": "Learn More"
    },
    {
      "type": "url",
      "id": "button_link",
      "label": "Button Link"
    },
    {
      "type": "image_picker",
      "id": "background_image",
      "label": "Background Image"
    },
    {
      "type": "color",
      "id": "background_color",
      "label": "Background Color",
      "default": "#ffffff"
    },
    { "type": "range", "id": "padding_top", "label": "Padding Top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "padding_bottom", "label": "Padding Bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "padding_left", "label": "Padding Left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "padding_right", "label": "Padding Right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "margin_top", "label": "Margin Top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "margin_bottom", "label": "Margin Bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "margin_left", "label": "Margin Left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
    { "type": "range", "id": "margin_right", "label": "Margin Right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" }
  ],
  "blocks": [
    {
      "type": "grid_box",
      "name": "Grid Box",
      "settings": [
        {
          "type": "checkbox",
          "id": "image_left",
          "label": "Image on Left?",
          "default": true
        },
        {
          "type": "image_picker",
          "id": "image",
          "label": "Box Image"
        },
        {
          "type": "range",
          "id": "image_width",
          "label": "Image Width (%)",
          "min": 10,
          "max": 100,
          "step": 5,
          "default": 40
        },
        {
          "type": "text",
          "id": "title",
          "label": "Title",
          "default": "Smarter Way to Watch"
        },
        {
          "type": "textarea",
          "id": "text",
          "label": "Text",
          "default": "No need for extra streaming boxesâ€”Google TV gives you access to over 700,000+ movies and shows."
        },
        {
          "type": "color",
          "id": "text_color",
          "label": "Text Color",
          "default": "#000000"
        },
        {
          "type": "range",
          "id": "text_size",
          "label": "Font Size (px)",
          "min": 10,
          "max": 40,
          "step": 1,
          "default": 16
        }
      ]
    }
  ],
  "presets": [
    {
      "name": "Somos Content/Image",
      "category": "Somos Custom"
    }
  ]
}
{% endschema %}

{% style %}
#shopify-section-{{ section.id }} {
  background-color: {{ section.settings.background_color }};
  {% if section.settings.background_image != blank %}
    background-image: url({{ section.settings.background_image | img_url: 'master' }});
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
  {% endif %}
  padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px;
  margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;
}
{% endstyle %}

<section class="icon-grid">
  <div class="icon-grid-heading" style="text-align: center; margin-bottom: 40px;">
    <h2>{{ section.settings.heading }}</h2>
    <p>{{ section.settings.description }}</p>
    {% if section.settings.button_label != blank %}
      <a href="{{ section.settings.button_link }}" class="button" style="display: inline-block; margin-top: 20px;">{{ section.settings.button_label }}</a>
    {% endif %}
  </div>

  {% for block in section.blocks %}
    <div class="icon-grid-row" style="display: flex; flex-wrap: wrap; align-items: center; margin: 30px 0;">
      {% if block.settings.image_left %}
        <div class="image-col" style="flex: 0 0 {{ block.settings.image_width }}%;">
          {% if block.settings.image %}
            <img src="{{ block.settings.image | img_url: 'master' }}" style="width: 100%;" />
          {% endif %}
        </div>
        <div class="text-col" style="flex: 1; padding-left: 20px; color: {{ block.settings.text_color }}; font-size: {{ block.settings.text_size }}px;">
          <h3>{{ block.settings.title }}</h3>
          <p>{{ block.settings.text }}</p>
        </div>
      {% else %}
        <div class="text-col" style="flex: 1; padding-right: 20px; color: {{ block.settings.text_color }}; font-size: {{ block.settings.text_size }}px;">
          <h3>{{ block.settings.title }}</h3>
          <p>{{ block.settings.text }}</p>
        </div>
        <div class="image-col" style="flex: 0 0 {{ block.settings.image_width }}%;">
          {% if block.settings.image %}
            <img src="{{ block.settings.image | img_url: 'master' }}" style="width: 100%;" />
          {% endif %}
        </div>
      {% endif %}
    </div>
  {% endfor %}
</section>
