{% style %}
  .stat-cards-grid {
    padding: 80px 0;
    background-color: #ffffff;
  }

  .stat-cards-grid__outer-wrapper {
    width: 996px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 20px;
  }

  .stat-cards-grid__row {
    display: flex;
    flex-direction: row;
    gap: 20px;
    width: 100%;
  }

  .stat-cards-grid__card {
    display: flex;
    /* height: 204px; */
    padding: 24px 32px;
    flex-direction: column;
    align-items: flex-start;
    gap: 10px;
    flex: 1 0 0;
    background-color: #FAFAFA;
  }

  .stat-cards-grid__inner {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    flex: 1 0 0;
    align-self: stretch;
    width: 100%;
    gap: 30px;
  }

  .stat-cards-grid__label {
    font-size: 16px;
    font-weight: 400;
    color: #2e2e2e;
  }

  .stat-cards-grid__stat {
    font-size: 48px;
    font-weight: 400;
    color: #0a0a0a;
    margin: 0;
  }

  .stat-cards-grid__description {
    font-size: 16px;
    color: #7e8182;
    line-height: 1.6;
    margin: 0;
  }

  @media screen and (max-width: 768px) {
    .stat-cards-grid__outer-wrapper {
      width: 100%;
      padding: 0 20px;
    }

    .stat-cards-grid__row {
      flex-direction: column;
      gap: 20px;
    }

    .stat-cards-grid__card {
      width: 100%;
    }

    .stat-cards-grid__stat {
      font-size: 36px;
    }
  }
{% endstyle %}

