+              {% else %}
+                <span>→</span>
+              {% endif %}
+            </div>
+          </a>
+        {% endfor %}
+      </div>
+    </div>
+  </div>
+</div>
+
+<style>
+  .support-section {
+    background-color: #20413B;
+    color: white;
+    padding: 40px;
+    display: flex;
+    justify-content: center;
+    flex-wrap: wrap;
+  }
+
+  .support-container {
+    display: flex;
+    width: 100%;
+    max-width: 1200px;
+    justify-content: space-between;
+    align-items: flex-start;
+    flex-wrap: wrap;
+  }
+
+  .support-left {
+    max-width: 300px;
+    flex-shrink: 0;
+  }
+
+  .support-title {
+    font-size: 24px;
+    font-weight: bold;
+    margin-bottom: 20px;
+  }
+
+  .support-button a {
+    display: inline-block;
+    padding: 10px 20px;
+    color: white;
+    border: 1px solid white;
+    text-decoration: none;
+    border-radius: 20px;
+    font-size: 14px;
+  }
+
+  .support-links {
+    margin-left: auto; /* pushes it to the far right */
+    display: flex;
+    flex-direction: column;
+    gap: 0;
+  }
+
+  .support-link {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    width: 300px;
+    color: white;
+    text-decoration: none;
+    padding: 16px 0;
+    border-bottom: 1px solid rgba(255, 255, 255, 0.08);
+  }
+
+  .support-link.last {
+    border-bottom: none;
+  }
+
+  .support-link .text {
+    font-size: 16px;
+  }
+
+  .support-link .icon-box {
+    border: 1px solid white;
+    padding: 10px;
+    border-radius: 4px;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    width: 40px;
+    height: 40px;
+  }
+
+  .support-link img {
+    width: 16px;
+    height: 16px;
+    object-fit: contain;
+  }
+</style>
+
+{% schema %}
+{
+  "name": "Support",
+  "settings": [
+    {
+      "type": "select",
+      "id": "section_width",
+      "label": "Section Width",
+      "default": "standard",
+      "options": [
+        { "value": "standard", "label": "50% Width" },
+        { "value": "full", "label": "Full Width" }
+      ]
+    },
+    {
+      "type": "text",
+      "id": "button_label",
+      "label": "Button label",
+      "default": "See All"
+    },
+    {
+      "type": "url",
+      "id": "button_link",
+      "label": "Button link"
