+  "blocks": [
+    {
+      "type": "link_item",
+      "name": "Link Item",
+      "settings": [
+        { "type": "text", "id": "text", "label": "Link Text", "default": "Contact Us" },
+        { "type": "url", "id": "url", "label": "Link URL" },
+        { "type": "image_picker", "id": "icon", "label": "Icon" }
