@@ -0,0 +1,167 @@
+<div class="support-section" style="padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px; margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px">
+  <div class="section_width_{{ section.settings.section_width }}">
+    <div class="support-container">
+      <div class="support-left">
+        <div class="support-title">Sylvox Support</div>
+        {% if section.settings.button_label and section.settings.button_link %}
+          <div class="support-button">
+            <a href="{{ section.settings.button_link }}">{{ section.settings.button_label }}</a>
+          </div>
+        {% endif %}
+      </div>
+
+      <div class="support-links">
+        {% for block in section.blocks %}
+          <a class="support-link{% if forloop.last %} last{% endif %}" href="{{ block.settings.url }}">
+            <div class="text">{{ block.settings.text }}</div>
+            <div class="icon-box">
+              {% if block.settings.icon != blank %}
+                <img src="{{ block.settings.icon | image_url: width: 32 }}" alt="icon">
+              {% else %}
+                <span>→</span>
+              {% endif %}
+            </div>
+          </a>
+        {% endfor %}
+      </div>
+    </div>
+  </div>
+</div>
+
+<style>
+  .support-section {
+    background-color: #20413B;
+    color: white;
+    padding: 40px;
+    display: flex;
+    justify-content: center;
+    flex-wrap: wrap;
+  }
+
+  .support-container {
+    display: flex;
+    width: 100%;
+    max-width: 1200px;
+    justify-content: space-between;
+    align-items: flex-start;
+    flex-wrap: wrap;
+  }
+
+  .support-left {
+    max-width: 300px;
+    flex-shrink: 0;
+  }
+
+  .support-title {
+    font-size: 24px;
+    font-weight: bold;
+    margin-bottom: 20px;
+  }
+
+  .support-button a {
+    display: inline-block;
+    padding: 10px 20px;
+    color: white;
+    border: 1px solid white;
+    text-decoration: none;
+    border-radius: 20px;
+    font-size: 14px;
+  }
+
+  .support-links {
+    margin-left: auto; /* pushes it to the far right */
+    display: flex;
+    flex-direction: column;
+    gap: 0;
+  }
+
+  .support-link {
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    width: 300px;
+    color: white;
+    text-decoration: none;
+    padding: 16px 0;
+    border-bottom: 1px solid rgba(255, 255, 255, 0.08);
+  }
+
+  .support-link.last {
+    border-bottom: none;
+  }
+
+  .support-link .text {
+    font-size: 16px;
+  }
+
+  .support-link .icon-box {
+    border: 1px solid white;
+    padding: 10px;
+    border-radius: 4px;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    width: 40px;
+    height: 40px;
+  }
+
+  .support-link img {
+    width: 16px;
+    height: 16px;
+    object-fit: contain;
+  }
+</style>
+
+{% schema %}
+{
+  "name": "Support",
+  "settings": [
+    {
+      "type": "select",
+      "id": "section_width",
+      "label": "Section Width",
+      "default": "standard",
+      "options": [
+        { "value": "standard", "label": "50% Width" },
+        { "value": "full", "label": "Full Width" }
+      ]
+    },
+    {
+      "type": "text",
+      "id": "button_label",
+      "label": "Button label",
+      "default": "See All"
+    },
+    {
+      "type": "url",
+      "id": "button_link",
+      "label": "Button link"
+    },
+    { "type": "range", "id": "padding_top", "label": "Padding top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "padding_bottom", "label": "Padding bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "padding_left", "label": "Padding left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "padding_right", "label": "Padding right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "margin_top", "label": "Margin top", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "margin_bottom", "label": "Margin bottom", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "margin_left", "label": "Margin left", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" },
+    { "type": "range", "id": "margin_right", "label": "Margin right", "min": 0, "max": 100, "step": 5, "default": 0, "unit": "px" }
+  ],
+  "blocks": [
+    {
+      "type": "link_item",
+      "name": "Link Item",
+      "settings": [
+        { "type": "text", "id": "text", "label": "Link Text", "default": "Contact Us" },
+        { "type": "url", "id": "url", "label": "Link URL" },
+        { "type": "image_picker", "id": "icon", "label": "Icon" }
+      ]
+    }
+  ],
+  "presets": [
+    {
+      "name": "Support",
+      "category": "Somos Custom"
+    }
+  ]
+}
+{% endschema %}
