@@ -1,232 +1,220 @@
+{% stylesheet %}
+.section_width_standard,
+.section_width_full {
+  transition: all 0.3s ease;
+}
+.section_width_full {
+  width: 100%;
+}
+.section_width_standard {
+  margin: 0 auto !important;
+  width: 90% !important;
+}
+
+.section_content_width_standard,
+.section_content_width_full {
+  transition: all 0.3s ease;
+}
+.section_content_width_full {
+  width: 100%;
+}
+.section_content_width_standard {
+  margin: 0 auto !important;
+  width: 90% !important;
+}
+{% endstylesheet %}
+
+{% assign background_style = '' %}
+{% if section.settings.bg_image != blank %}
+  {% assign background_style = 'background-image: url(' | append: section.settings.bg_image | img_url: 'master' | append: ');' %}
+{% endif %}
+
+<div
+  class="section_width_{{ section.settings.section_width }}"
+  style="background-color: {{ section.settings.bg_color }}; {{ background_style }} padding: {{ section.settings.padding_top }}px {{ section.settings.padding_right }}px {{ section.settings.padding_bottom }}px {{ section.settings.padding_left }}px; margin: {{ section.settings.margin_top }}px {{ section.settings.margin_right }}px {{ section.settings.margin_bottom }}px {{ section.settings.margin_left }}px;"
+>
+  <div class="section_content_width_{{ section.settings.section_content_width }}">
+    <div class="flex flex-col md:flex-row items-center gap-10 md:gap-20">
+      <div class="w-full md:w-1/2">
+        <img src="{{ section.settings.left_image | img_url: 'master' }}" alt="Left Image" style="width: {{ section.settings.left_image_width }}%;">
+      </div>
+      <div class="w-full md:w-1/2 text-left">
+        {% if section.settings.icon %}
+          <img src="{{ section.settings.icon | img_url: 'master' }}" alt="Heading Icon" class="mb-4" style="width: 40px;">
+        {% endif %}
+        <h2 style="color: {{ section.settings.heading_color }};">{{ section.settings.heading }}</h2>
+        <p style="color: {{ section.settings.text_color }};">{{ section.settings.text }}</p>
+      </div>
+    </div>
+
+    {% if section.blocks.size > 0 %}
+      <div class="grid grid-cols-1 md:grid-cols-3 gap-10 mt-12">
+        {% for block in section.blocks %}
+          <div class="flex flex-col items-center text-center gap-3">
+            {% if block.settings.icon %}
+              <img src="{{ block.settings.icon | img_url: 'master' }}" alt="USP Icon" style="width: {{ block.settings.icon_size }}%;">
+            {% endif %}
+            <h3 style="color: {{ block.settings.title_color }};">{{ block.settings.title }}</h3>
+            {% if block.settings.subtitle %}
+              <p style="color: {{ block.settings.subtitle_color }}; font-style: italic; margin-top: -0.25rem;">{{ block.settings.subtitle }}</p>
+            {% endif %}
+            <p style="color: {{ block.settings.body_color }};">{{ block.settings.body }}</p>
+          </div>
+        {% endfor %}
+      </div>
+    {% endif %}
+  </div>
+</div>
+
