{% stylesheet %}
.section_width_standard,
.section_width_full {
  transition: all 0.3s ease;
}
.section_width_full {
  width: 100%;
}
.section_width_standard {
  margin: 0 auto !important;
  width: 90% !important;
}

.section_content_width_standard,
.section_content_width_full {
  transition: all 0.3s ease;
}
.section_content_width_full {
  width: 100%;
}
.section_content_width_standard {
  margin: 0 auto !important;
  width: 90% !important;
}
{% endstylesheet %}

<div
  class="section_width_{{ section.settings.section_width }}"
  style="background-color: {{ section.settings.bg_color }}; background-image: url({{ section.settings.bg_image | img_url: 'master' }}); margin: {{ section.settings.margin_top }} {{ section.settings.margin_right }} {{ section.settings.margin_bottom }} {{ section.settings.margin_left }}; padding: {{ section.settings.padding_top }} {{ section.settings.padding_right }} {{ section.settings.padding_bottom }} {{ section.settings.padding_left }};"
>
  <div class="section_content_width_{{ section.settings.section_content_width }}">
    <div class="flex flex-col md:flex-row gap-6 items-center">
      <div class="w-full md:w-1/2">
        <img src="{{ section.settings.left_image | img_url: 'master' }}" alt="Left Image" style="width: {{ section.settings.left_image_width }}%;">
      </div>
      <div class="w-full md:w-1/2">
        <h2 style="color: {{ section.settings.heading_color }}; font-size: {{ section.settings.heading_size }}px;">
          {{ section.settings.heading }}
        </h2>
        <p style="color: {{ section.settings.text_color }}; font-size: {{ section.settings.text_size }}px;">
          {{ section.settings.text }}
        </p>
      </div>
    </div>

    {% if section.blocks.size > 0 %}
      <div class="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
        {% for block in section.blocks %}
          <div class="text-center">
            <img src="{{ block.settings.icon | img_url: 'master' }}" alt="Icon" style="width: {{ block.settings.icon_size }}%;">
            <h3 style="color: {{ block.settings.title_color }}; font-size: {{ block.settings.title_size }}px;">
              {{ block.settings.title }}
            </h3>
            <p style="color: {{ block.settings.body_color }}; font-size: {{ block.settings.body_size }}px;">
              {{ block.settings.body }}
            </p>
          </div>
        {% endfor %}
      </div>
    {% endif %}
  </div>
</div>

{% schema %}
{
  "name": "Various Grid",
  "settings": [
    {
      "type": "image_picker",
      "id": "bg_image",
      "label": "Background Image"
    },
    {
      "type": "color",
      "id": "bg_color",
      "label": "Background Color",
      "default": "#ffffff"
    },
    {
      "type": "text",
      "id": "margin_top",
      "label": "Margin Top",
      "default": "0"
    },
    {
      "type": "text",
      "id": "margin_right",
      "label": "Margin Right",
      "default": "0"
    },
    {
      "type": "text",
      "id": "margin_bottom",
      "label": "Margin Bottom",
      "default": "0"
    },
    {
      "type": "text",
      "id": "margin_left",
      "label": "Margin Left",
      "default": "0"
    },
    {
      "type": "text",
      "id": "padding_top",
      "label": "Padding Top",
      "default": "0"
    },
    {
      "type": "text",
      "id": "padding_right",
      "label": "Padding Right",
      "default": "0"
    },
    {
      "type": "text",
      "id": "padding_bottom",
      "label": "Padding Bottom",
      "default": "0"
    },
    {
      "type": "text",
      "id": "padding_left",
      "label": "Padding Left",
      "default": "0"
    },
    {
      "type": "image_picker",
      "id": "left_image",
      "label": "Left Image"
    },
    {
      "type": "range",
      "id": "left_image_width",
      "label": "Left Image Width (%)",
      "min": 10,
      "max": 100,
      "step": 1,
      "default": 100
    },
    {
      "type": "text",
      "id": "heading",
      "label": "Heading",
      "default": "Stay Connected, Indoors or Out."
    },
    {
      "type": "color",
      "id": "heading_color",
      "label": "Heading Color",
      "default": "#000000"
    },
    {
      "type": "range",
      "id": "heading_size",
      "label": "Heading Font Size (px)",
      "min": 10,
      "max": 48,
      "default": 24
    },
    {
      "type": "textarea",
      "id": "text",
      "label": "Text",
      "default": "No matter how you watch, listen, or play, your TV should keep up."
    },
    {
      "type": "color",
      "id": "text_color",
      "label": "Text Color",
      "default": "#333333"
    },
    {
      "type": "range",
      "id": "text_size",
      "label": "Text Font Size (px)",
      "min": 10,
      "max": 24,
      "default": 16
    },
    {
      "type": "select",
      "id": "section_width",
      "label": "Section Width",
      "default": "standard",
      "options": [
        { "value": "standard", "label": "50% Width" },
        { "value": "full", "label": "Full Width" }
      ]
    },
    {
      "type": "select",
      "id": "section_content_width",
      "label": "Content Width",
      "default": "standard",
      "options": [
        { "value": "standard", "label": "50% Width" },
        { "value": "full", "label": "Full Width" }
      ]
    }
  ],
  "blocks": [
    {
      "type": "feature_box",
      "name": "USP Box",
      "settings": [
        {
          "type": "image_picker",
          "id": "icon",
          "label": "Icon"
        },
        {
          "type": "range",
          "id": "icon_size",
          "label": "Icon Width (%)",
          "min": 10,
          "max": 100,
          "default": 50
        },
        {
          "type": "text",
          "id": "title",
          "label": "Title",
          "default": "Wireless Freedom"
        },
        {
          "type": "color",
          "id": "title_color",
          "label": "Title Color",
          "default": "#000000"
        },
        {
          "type": "range",
          "id": "title_size",
          "label": "Title Font Size (px)",
          "min": 12,
          "max": 36,
          "default": 18
        },
        {
          "type": "textarea",
          "id": "body",
          "label": "Body",
          "default": "Ditch the cables and enjoy true wireless convenience."
        },
        {
          "type": "color",
          "id": "body_color",
          "label": "Body Color",
          "default": "#333333"
        },
        {
          "type": "range",
          "id": "body_size",
          "label": "Body Font Size (px)",
          "min": 10,
          "max": 24,
          "default": 14
        }
      ]
    }
  ],
  "presets": [
    {
      "name": "Various Grid",
      "category": "Somos Custom"
    }
  ]
}
{% endschema %}
