commits.content:
{%- liquid
  assign context = context | default: blank
  assign collection = collection | default: settings.cart_collection
-%}

{%- if collection != blank -%}
  <link rel="stylesheet" href="https://unpkg.com/flickity@2/dist/flickity.min.css">
  <script src="https://unpkg.com/flickity@2/dist/flickity.pkgd.min.js" defer></script>

  <style>
    .flickity-cart-slider {
      padding: 40px 0;
    }

    .flickity-cart-slider .video-swiper-content {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 20px;
    }

    .flickity-cart-slider .usp-subtext {
      font-size: 1.5rem;
      font-weight: 600;
      margin: 0;
    }

    .flickity-cart-slider .content-right {
      display: flex;
      gap: 10px;
    }

    .flickity-cart-slider .usp-arrow {
      width: 40px;
      height: 40px;
      border: 1px solid #636363;
      border-radius: 50%;
      background: none;
      cursor: pointer;
      transition: all 0.3s ease-out;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 30px;
      color: #636363;
      line-height: 1;
      padding: 0;
    }

    .flickity-cart-slider .usp-arrow:hover {
      background: #297f67;
      color: #fff;
      border: none;
    }

    .flickity-carousel {
      width: 100%;
    }

    .flickity-carousel .carousel-cell {
      width: 285px;
      margin-right: 15px;
    }

    .flickity-enabled {
      outline: none;
    }

    /* Optional: center dots */
    .flickity-page-dots {
      text-align: left;
      margin-top: 10px;
    }

    .flickity-page-dots .dot {
      width: 8px;
      height: 8px;
      margin: 0 5px;
      background: #ccc;
      border-radius: 50%;
      display: inline-block;
    }

    .flickity-page-dots .dot.is-selected {
      background: #297f67;
    }
  </style>

  <div class="cart__item-row cart-recommendations flickity-cart-slider" data-location="{{ context }}">
    <div class="video-swiper-content">
      <div class="content-left">
        <div class="usp-subtext">You May Also Like</div>
      </div>
      <div class="content-right">
        <button class="usp-arrow prev">‹</button>
        <button class="usp-arrow next">›</button>
      </div>
    </div>

    <div class="flickity-carousel">
      {%- for product in collection.products limit: 12 -%}
        <div class="carousel-cell">
          {% render 'product-grid-item', product: product, collection: collection, sizes: '285px' %}
        </div>
      {%- endfor -%}
    </div>
  </div>

  <script>
    document.addEventListener('DOMContentLoaded', function () {
      const flktySlider = document.querySelector('.flickity-carousel');
      if (!flktySlider) return;

      const flkty = new Flickity(flktySlider, {
        cellAlign: 'left',
        contain: true,
        groupCells: true,
        wrapAround: false,
        pageDots: true,
        prevNextButtons: false
      });

      const prevButton = document.querySelector('.usp-arrow.prev');
      const nextButton = document.querySelector('.usp-arrow.next');

      prevButton.addEventListener('click', () => flkty.previous());
      nextButton.addEventListener('click', () => flkty.next());
    });
  </script>
{%- endif -%}
